import React, { useState, useEffect, useRef } from "react";
import * as go from "gojs";

const GraphVisualization = ({ graphData, dimensions, setDimensions }) => {
  const diagramRef = useRef();

  useEffect(() => {
    if (graphData) {
      const $ = go.GraphObject.make;

      // Dispose of the old diagram if it exists
      if (diagramRef.current) {
        const existingDiagram = go.Diagram.fromDiv(diagramRef.current);
        if (existingDiagram) {
          existingDiagram.div = null; // Clears the previous diagram instance
        }
      }

      const myDiagram = $(go.Diagram, diagramRef.current, {
        "undoManager.isEnabled": true,
        layout: $(go.TreeLayout, { angle: 0, layerSpacing: 35 }),
      });

      // Node template with hover functionality
      myDiagram.nodeTemplate = $(
        go.Node,
        "Auto",
        $(go.Shape, "RoundedRectangle", { fill: "lightblue" }, new go.Binding("fill", "color")),
        $(go.TextBlock, { margin: 5 }, new go.Binding("text", "label")), // Always show the node label
        $(
          go.TextBlock,
          {
            margin: 5,
            visible: false, // HoverLabel hidden by default
            font: "italic 10pt sans-serif",
            stroke: "gray",
            name: "HOVER_LABEL",
            background: "lightyellow",
            maxWidth: 200, // Limit the max width of the popup
            wrap: go.TextBlock.WrapFit, // Wrap text
            alignment: go.Spot.BottomCenter,
          },
          new go.Binding("text", "hoverLabel")
        ),
        {
          // Mouse enter: show hover label
          mouseEnter: (e, obj) => {
            const hoverLabel = obj.findObject("HOVER_LABEL");
              if(hoverLabel){
                 hoverLabel.visible = true;
                    const nodeWidth = obj.actualBounds?.width;
                    if(nodeWidth) {
                       hoverLabel.maxWidth = nodeWidth;
                    }
              }
          },
          // Mouse leave: hide hover label
          mouseLeave: (e, obj) => {
             const hoverLabel = obj.findObject("HOVER_LABEL");
            if (hoverLabel) hoverLabel.visible = false;
          },
        }
      );

      myDiagram.model = new go.GraphLinksModel(graphData.nodeDataArray, graphData.linkDataArray);

      myDiagram.addDiagramListener("InitialLayoutCompleted", () => {
        const bounds = myDiagram.documentBounds;
        setDimensions({
          width: `${Math.ceil(bounds.width)}px`,
          height: `${Math.ceil(bounds.height)}px`,
        });
      });
    }
  }, [graphData, setDimensions]);


  return (
    <div
      ref={diagramRef}
      style={{
        width: "100%",
        height: dimensions.height,
        border: "1px solid black",
      }}
    >
      {!graphData && <p>Graph will be displayed here after uploading a file.</p>}
    </div>
  );
};

export default GraphVisualization;
