import React from "react";
import GraphVisualization from "./GraphVisualization";

const DiagramPopup = ({ 
    graphData, 
    dimensions, 
    setDimensions, 
    isDiagramMinimized, 
    toggleMinimizeDiagram, 
    toggleDiagram 
}) => {
    return (
        <div style={{
            position: "fixed",
            bottom: isDiagramMinimized ? "0" : "20px",
            right: "20px",
            width: isDiagramMinimized ? "300px" : "800px",
            height: isDiagramMinimized ? "40px" : "600px",
            backgroundColor: "white",
            borderRadius: "8px",
            boxShadow: "0 4px 20px rgba(0, 0, 0, 0.15)",
            overflow: "hidden",
            zIndex: 1000,
            transition: "all 0.3s ease",
            display: "flex",
            flexDirection: "column"
        }}>
            {/* Popup Header */}
            <div style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                padding: "12px 16px",
                borderBottom: isDiagramMinimized ? "none" : "1px solid #e2e8f0",
                backgroundColor: "#ef4444",
                color: "white",
                borderTopLeftRadius: "8px",
                borderTopRightRadius: "8px"
            }}>
                <h3 style={{ margin: 0, fontSize: "1rem" }}>VP Preview</h3>
                <div style={{ display: "flex", gap: "8px" }}>
                    <button
                        onClick={toggleMinimizeDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            color: "white",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            width: "24px",
                            height: "24px"
                        }}
                    >
                        {isDiagramMinimized ? (
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                <polyline points="5 15 12 8 19 15"></polyline>
                            </svg>
                        ) : (
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                <polyline points="5 9 12 16 19 9"></polyline>
                            </svg>
                        )}
                    </button>
                    <button
                        onClick={toggleDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            color: "white",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            width: "24px",
                            height: "24px"
                        }}
                    >
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                            <line x1="18" y1="6" x2="6" y2="18"></line>
                            <line x1="6" y1="6" x2="18" y2="18"></line>
                        </svg>
                    </button>
                </div>
            </div>

            {/* Popup Content */}
            {!isDiagramMinimized && (
                <div style={{ 
                    flexGrow: 1,
                    padding: "16px",
                    overflow: "auto"
                }}>
                    <GraphVisualization
                        graphData={graphData}
                        dimensions={dimensions}
                        setDimensions={setDimensions}
                    />
                </div>
            )}
        </div>
    );
};

export default DiagramPopup;
