import { useState } from "react";
import axios from "axios";

const useFileUpload = (setGraphData) => {
    const [file, setFile] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    // Handle file change
    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    // Fetch graph data with loading state
    const fetchGraphData = async (showNotification) => {
        setIsLoading(true);
        try {
            const response = await axios.get("http://localhost:8000/graph-data/");
            setGraphData(response.data);
        } catch (error) {
            console.error("Error fetching graph data:", error);
            if (showNotification) {
                showNotification("Error fetching graph data.", "error");
            }
        } finally {
            setIsLoading(false);
        }
    };

    // Handle file upload with loading state
    const handleFileUpload = async (showNotification) => {
        if (!file) {
            showNotification("Please select a file before uploading.", "error");
            return;
        }

        setIsLoading(true);
        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await axios.post("http://localhost:8000/upload/", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            showNotification(response.data.message, "success");
            await fetchGraphData();
        } catch (error) {
            console.error("Error uploading file:", error);
            let errorMessage = "Error uploading file.";
            if (error.response && error.response.data) {
                errorMessage += ` ${error.response.data.detail || error.response.data.message}`;
            }
            showNotification(errorMessage, "error");
        } finally {
            setIsLoading(false);
        }
    };

    return {
        file,
        isLoading,
        handleFileChange,
        handleFileUpload,
        fetchGraphData
    };
};

export default useFileUpload;
