import React, { useState, useEffect } from "react";
import axios from "axios";
import * as go from "gojs";

const App = () => {
  const [graphData, setGraphData] = useState({ nodeDataArray: [], linkDataArray: [] });
  const [selectedLabel, setSelectedLabel] = useState("");
  const [isPopupVisible, setIsPopupVisible] = useState(false);
  const [popupData, setPopupData] = useState(null);

  useEffect(() => {
    fetchGraphData();
  }, []);

  const fetchGraphData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/graph-data/");
      setGraphData(response.data);
    } catch (error) {
      console.error("Error fetching graph data:", error);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(response.data.message);
      fetchGraphData();
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Error uploading file. Please check the console for details.");
    }
  };

  const handleDragEnd = async (sourceKey, targetKey, sourceType, targetType) => {
    try {
      const response = await axios.post("http://localhost:8000/apply-drag-drop/", {
        source: sourceKey,
        target: targetKey,
        sourceType,
        targetType,
      });
      console.log(response.data.message);
      fetchGraphData(); // Refresh data after drag-and-drop
    } catch (error) {
      console.error("Error applying drag-and-drop:", error);
    }
  };

  const handleLabelEdit = (nodeKey, newLabel) => {
    // Update the label in graphData
    const updatedNodes = graphData.nodeDataArray.map((node) =>
      node.key === nodeKey ? { ...node, label: newLabel } : node
    );
    setGraphData((prevState) => ({
      ...prevState,
      nodeDataArray: updatedNodes,
    }));
  };

  const handlePopupOpen = (nodeKey, label) => {
    setPopupData({ nodeKey, label });
    setIsPopupVisible(true);
  };

  const handlePopupSave = () => {
    if (popupData) {
      handleLabelEdit(popupData.nodeKey, selectedLabel);
    }
    setIsPopupVisible(false);
    setPopupData(null);
  };

  const handlePopupCancel = () => {
    setIsPopupVisible(false);
    setPopupData(null);
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Left Side: File Upload and Table Visualization */}
      <div style={{ flex: 1, overflow: "auto" }}>
        {/* File Upload */}
        <div style={{ marginBottom: "20px" }}>
          <input type="file" onChange={handleFileUpload} />
        </div>

        {/* Table Visualization */}
        <table style={{ width: "100%", border: "1px solid black", textAlign: "left" }}>
          <thead>
            <tr>
              <th>Cluster</th>
              <th>Object</th>
              <th>Attribute</th>
            </tr>
          </thead>
          <tbody>
            {graphData.nodeDataArray
              .filter((node) => node.category !== "cluster")
              .map((node) => (
                <tr key={node.key}>
                  <td>
                    {node.category === "object" ? (
                      <span>
                        {node.label}{" "}
                        <button onClick={() => handlePopupOpen(node.key, node.label)}>+</button>
                      </span>
                    ) : null}
                  </td>
                  <td>
                    {node.category === "attribute" ? (
                      <span>
                        {node.label}{" "}
                        <button onClick={() => handlePopupOpen(node.key, node.label)}>+</button>
                      </span>
                    ) : null}
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>

      {/* Right Side: Graph Visualization */}
      <div style={{ flex: 1, overflow: "auto" }}>
        <div id="graph-container" style={{ width: "100%", height: "100%" }}></div>
      </div>

      {/* Popup for Label Editing */}
      {isPopupVisible && (
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            backgroundColor: "white",
            padding: "20px",
            border: "1px solid black",
          }}
        >
          <textarea
            value={selectedLabel}
            onChange={(e) => setSelectedLabel(e.target.value)}
            style={{ width: "100%", height: "100px" }}
          />
          <div style={{ marginTop: "10px" }}>
            <button onClick={handlePopupSave}>Save</button>
            <button onClick={handlePopupCancel}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
