import React, { useState, useEffect, useRef } from "react";
import TableGraphVisualization from "./TableGraphVisualization";

/**
 * DiagramPopup component that displays a table-based visualization
 * of the graph data with 3NF compliance check feature
 */
const DiagramPopup = ({ 
    graphData, 
    dimensions, 
    setDimensions, 
    isDiagramMinimized, 
    toggleMinimizeDiagram, 
    toggleDiagram 
}) => {
    // Start maximized by default
    const [isMaximized, setIsMaximized] = useState(true);
    const containerRef = useRef(null);
    
    // Sanitize the graph data for safety
    const safeGraphData = graphData || { nodeDataArray: [], linkDataArray: [] };
    
    const toggleMaximize = () => {
        setIsMaximized(prev => !prev);
    };
    
    // Reset dimensions when transitioning to maximized state
    useEffect(() => {
        if (isMaximized) {
            setDimensions({ 
                width: `${window.innerWidth}px`, 
                height: `${window.innerHeight - 50}px` 
            });
        }
    }, [isMaximized, setDimensions]);
    
    // Determine popup styles based on state
    let popupStyle;
    
    if (isMaximized) {
        // Fully maximized takes the entire viewport
        popupStyle = {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            width: "100vw",
            height: "100vh",
            zIndex: 1000,
            backgroundColor: "white",
            overflow: "hidden",
            display: "flex",
            flexDirection: "column"
        };
    } else if (isDiagramMinimized) {
        // Minimized state at bottom right
        popupStyle = {
            position: "fixed",
            bottom: 0,
            right: 0,
            width: "300px",
            height: "40px",
            zIndex: 1000,
            backgroundColor: "white",
            borderTopLeftRadius: "8px",
            boxShadow: "0 4px 20px rgba(0, 0, 0, 0.15)",
            overflow: "hidden"
        };
    } else {
        // Normal state with set dimensions in bottom right
        popupStyle = {
            position: "fixed",
            bottom: "20px",
            right: "20px",
            width: dimensions?.width || "800px",
            height: dimensions?.height || "600px",
            zIndex: 1000,
            backgroundColor: "white",
            borderRadius: "8px",
            boxShadow: "0 4px 20px rgba(0, 0, 0, 0.15)",
            overflow: "hidden",
            display: "flex", 
            flexDirection: "column"
        };
    }
    
    return (
        <div ref={containerRef} style={popupStyle}>
            {/* Popup Header */}
            <div style={{ 
                display: "flex", 
                justifyContent: "space-between", 
                alignItems: "center", 
                padding: "14px 16px",
                borderBottom: "1px solid #e5e7eb",
                backgroundColor: "#f1f5f9",
                flexShrink: 0,
                height: "50px",
                boxSizing: "border-box"
            }}>
                <div style={{ 
                    display: "flex", 
                    alignItems: "center", 
                    gap: "12px" 
                }}>
                    {/* Diamond Icon */}
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        width="20" 
                        height="20" 
                        viewBox="0 0 24 24" 
                        fill="#ef4444" 
                        stroke="#c2410c" 
                        strokeWidth="1.5" 
                        strokeLinecap="round" 
                        strokeLinejoin="round"
                    >
                        <polygon points="12 2 22 12 12 22 2 12 12 2" />
                    </svg>
                    
                    <h3 style={{ 
                        margin: 0, 
                        color: "#1e293b", 
                        fontSize: "1rem", 
                        fontWeight: "600" 
                    }}>
                        Table Visualization with 3NF Analysis
                    </h3>
                </div>
                
                <div style={{ 
                    display: "flex", 
                    gap: "8px" 
                }}>
                    {/* Minimize/Restore Button */}
                    <button
                        onClick={toggleMinimizeDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            padding: "4px",
                            borderRadius: "4px"
                        }}
                        title={isDiagramMinimized ? "Restore" : "Minimize"}
                    >
                        <svg 
                            xmlns="http://www.w3.org/2000/svg" 
                            width="16" 
                            height="16" 
                            viewBox="0 0 24 24" 
                            fill="none" 
                            stroke="#64748b" 
                            strokeWidth="2" 
                            strokeLinecap="round" 
                            strokeLinejoin="round"
                        >
                            {isDiagramMinimized 
                                ? <polyline points="7 13 12 18 17 13"></polyline> 
                                : <polyline points="7 11 12 6 17 11"></polyline>}
                        </svg>
                    </button>
                    
                    {/* Maximize/Restore Button */}
                    {!isDiagramMinimized && (
                        <button
                            onClick={toggleMaximize}
                            style={{
                                background: "none",
                                border: "none",
                                cursor: "pointer",
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                padding: "4px",
                                borderRadius: "4px"
                            }}
                            title={isMaximized ? "Restore" : "Maximize"}
                        >
                            <svg 
                                xmlns="http://www.w3.org/2000/svg" 
                                width="16" 
                                height="16" 
                                viewBox="0 0 24 24" 
                                fill="none" 
                                stroke="#64748b" 
                                strokeWidth="2" 
                                strokeLinecap="round" 
                                strokeLinejoin="round"
                            >
                                {isMaximized ? (
                                    <>
                                        <path d="M8 3H5a2 2 0 0 0-2 2v3m18 0V5a2 2 0 0 0-2-2h-3m0 18h3a2 2 0 0 0 2-2v-3M3 16v3a2 2 0 0 0 2 2h3" />
                                    </>
                                ) : (
                                    <>
                                        <path d="M8 3v3a2 2 0 0 1-2 2H3m18 0h-3a2 2 0 0 1-2-2V3m0 18v-3a2 2 0 0 1 2-2h3M3 16h3a2 2 0 0 1 2 2v3" />
                                    </>
                                )}
                            </svg>
                        </button>
                    )}
                    
                    {/* Close Button */}
                    <button
                        onClick={toggleDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            padding: "4px",
                            borderRadius: "4px"
                        }}
                        title="Close"
                    >
                        <svg 
                            xmlns="http://www.w3.org/2000/svg" 
                            width="16" 
                            height="16" 
                            viewBox="0 0 24 24" 
                            fill="none" 
                            stroke="#64748b" 
                            strokeWidth="2" 
                            strokeLinecap="round" 
                            strokeLinejoin="round"
                        >
                            <line x1="18" y1="6" x2="6" y2="18"></line>
                            <line x1="6" y1="6" x2="18" y2="18"></line>
                        </svg>
                    </button>
                </div>
            </div>
            
            {/* Popup Content */}
            {!isDiagramMinimized && (
                <div style={{ 
                    flexGrow: 1,
                    overflow: "hidden",
                    height: isMaximized ? "calc(100vh - 50px)" : "calc(100% - 50px)",
                    position: "relative"
                }}>
                    <TableGraphVisualization
                        graphData={safeGraphData}
                        dimensions={{
                            width: "100%",
                            height: "100%"
                        }}
                    />
                </div>
            )}
        </div>
    );
};

export default DiagramPopup;
