import React from "react";
import FileUpload from "./FileUpload";
import { buttonStyle } from "../utils/styles";

const ControlPanel = ({ 
    isLoading, 
    handleFileChange, 
    handleFileUpload, 
    loadAttributeDescriptions, 
    toggleHarmonisedColumn,
    toggleDiagram,
    showHarmonisedColumn,
    showDiagram
}) => {
    return (
        <div style={{ 
            display: "flex", 
            justifyContent: "space-between", 
            alignItems: "center",
            marginBottom: "16px"
        }}>
            <h2 style={{ margin: 0, fontSize: "1.25rem", color: "#1e293b" }}>Node Table</h2>
            <div style={{ 
                display: "flex", 
                gap: "8px" 
            }}>
                {/* File Upload Component */}
                <FileUpload 
                    onFileChange={handleFileChange} 
                    onFileUpload={handleFileUpload} 
                    isLoading={isLoading}
                />
                
                {/* Generate Definitions Button */}
                <button 
                    onClick={loadAttributeDescriptions}
                    disabled={isLoading}
                    style={{
                        ...buttonStyle(isLoading),
                        backgroundColor: "#6366f1",
                    }}
                >
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        width="16" 
                        height="16" 
                        viewBox="0 0 24 24" 
                        fill="none" 
                        stroke="currentColor" 
                        strokeWidth="2" 
                        strokeLinecap="round" 
                        strokeLinejoin="round"
                    >
                        <rect x="3" y="11" width="18" height="11" rx="2" ry="2"></rect>
                        <path d="M9 11V6a3 3 0 0 1 6 0v5"></path>
                        <circle cx="8" cy="16" r="1"></circle>
                        <circle cx="16" cy="16" r="1"></circle>
                    </svg>
                    Generate Definitions
                </button>
                
                {/* Harmonise Attributes Button */}
                <button 
                    onClick={toggleHarmonisedColumn}
                    disabled={isLoading}
                    style={{
                        ...buttonStyle(isLoading),
                        backgroundColor: showHarmonisedColumn ? "#1e40af" : "#3b82f6",
                    }}
                >
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        width="16" 
                        height="16" 
                        viewBox="0 0 24 24" 
                        fill="none" 
                        stroke="currentColor" 
                        strokeWidth="2" 
                        strokeLinecap="round" 
                        strokeLinejoin="round"
                    >
                        <line x1="8" y1="6" x2="21" y2="6"></line>
                        <line x1="8" y1="12" x2="21" y2="12"></line>
                        <line x1="8" y1="18" x2="21" y2="18"></line>
                        <line x1="3" y1="6" x2="3.01" y2="6"></line>
                        <line x1="3" y1="12" x2="3.01" y2="12"></line>
                        <line x1="3" y1="18" x2="3.01" y2="18"></line>
                    </svg>
                    Harmonise Attributes
                </button>
                
                {/* VP Preview Button */}
                <button 
                    onClick={toggleDiagram}
                    disabled={isLoading}
                    style={{
                        ...buttonStyle(isLoading),
                        backgroundColor: showDiagram ? "#c2410c" : "#ef4444",
                    }}
                >
                    {/* Red Diamond Icon */}
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        width="16" 
                        height="16" 
                        viewBox="0 0 24 24" 
                        fill="none" 
                        stroke="currentColor" 
                        strokeWidth="2" 
                        strokeLinecap="round" 
                        strokeLinejoin="round"
                    >
                        <polygon points="12 2 22 12 12 22 2 12 12 2" fill="#ffcccb" />
                    </svg>
                    VP Preview
                </button>
            </div>
        </div>
    );
};

export default ControlPanel;
