import React, { useState, useRef, useCallback, useEffect } from 'react';
import axios from 'axios';
import './BootstrapNestedList.css'; // Import the css file for this component

const NestedList = ({ data, graphData, openPopup, setGraphData, fetchGraphData }) => {
    const [draggedItem, setDraggedItem] = useState(null);
    const inputRefs = useRef({});
    const [expandedItems, setExpandedItems] = useState({});

     useEffect(() => {
           if (data) {
            const initialExpanded = data.reduce((acc, node) => {
              if (node.category === "system" || node.category === "object") acc[node.key] = true;
              return acc;
            }, {});
              setExpandedItems(initialExpanded)
          }
         }, [data]);

    const toggleItem = (key) => {
         setExpandedItems((prevExpandedItems) => ({
             ...prevExpandedItems,
             [key]: !prevExpandedItems[key],
         }));
     };
     const handleDragStart = (event, key) => {
            event.dataTransfer.setData("text/plain", key);
            setDraggedItem(key);
        };

        const handleDragOver = (event) => {
            event.preventDefault();
        };

         const handleDrop = async (event, targetKey, targetCategory) => {
            event.preventDefault();
            const sourceKey = event.dataTransfer.getData("text/plain");

            if (sourceKey && sourceKey !== targetKey) {
              setGraphData(async (prev) => {
                    let updatedNodeDataArray = [...prev.nodeDataArray];
                    let updatedLinkDataArray = [...prev.linkDataArray];

                    // Find the source node and target node.
                    const sourceNode = updatedNodeDataArray.find(node => node.key === sourceKey);
                    const targetNode = updatedNodeDataArray.find(node => node.key === targetKey);
                     if (sourceNode && targetNode) {
                        // Remove all existing links where the source node is the to
                       updatedLinkDataArray = updatedLinkDataArray.filter(link => link.to === sourceKey);
                     // If target is object, create new link
                        if (sourceNode.category === "attribute" && targetNode.category === "object" ) {
                             updatedLinkDataArray.push({ from: targetKey, to: sourceKey });
                         }
                         else if (sourceNode.category === "object" && targetNode.category === "system") {
                                  updatedLinkDataArray.push({ from: targetKey, to: sourceKey });
                          }
                      try {
                            const response = await axios.post("http://localhost:8000/apply-drag-drop/", {
                                source: sourceKey,
                                target: targetKey,
                                sourceType: sourceNode.category,
                                targetType: targetNode.category
                            });
                            if (response.data.message === "Drag-and-drop operation completed successfully") {
                                 fetchGraphData();
                            }
                        } catch (error) {
                              if (error.response) {
                                    console.error("Error applying drag and drop:", error.response.status, error.response.data);
                                    alert(`Error applying drag and drop. Status: ${error.response.status}, Message: ${error.response.data.detail}`);
                                } else if (error.request) {
                                    console.error("Error applying drag and drop:", error.request);
                                    alert("Error applying drag and drop. No response from server");
                                } else {
                                    console.error("Error applying drag and drop:", error.message);
                                    alert(`Error applying drag and drop: ${error.message}`);
                                }
                          }
                    }

                    return {
                        ...prev,
                        nodeDataArray: updatedNodeDataArray,
                        linkDataArray: updatedLinkDataArray,
                    };
                });

            }
            setDraggedItem(null);
         };

          const getDisplayText = (text) => {
                if (!text) return "";
                const lines = text.split('\n');
                const firstTwoLines = lines.slice(0, 2).join('\n');
                return (
                  <div style={{ position: 'relative', display: 'inline-block' }}>
                     <div style={{  overflow: 'hidden', whiteSpace: 'pre-line',  maxWidth:"calc(100% - 20px)", marginRight: "2px" }}
                       dangerouslySetInnerHTML={{__html: firstTwoLines.substring(0, 100) }}
                    />
                   <img
                     src="/zoom-in.png"
                     alt="Click to expand"
                     style={{ position: 'absolute', bottom: '2px', right: '2px', width: "16px", height: "16px", cursor: "pointer" }}
                   />
                </div>
              );
        };

         const handleClickInput = (key, currentLabel, currentName) => {
               openPopup(key, currentLabel, currentName);
              if (inputRefs.current[key] && inputRefs.current[key].current) {
                  inputRefs.current[key].current.focus();
                }
           }

        const setInputRef = (key) => {
           return inputRefs.current[key] ? inputRefs.current[key] :  (inputRefs.current[key] = React.createRef())
          }

        const renderNestedList = (items, level = 0) => {
            if (!items) {
                return null;
            }
             const getIndentClass = (level) => {
               return `list-group-item-indent-${level}`;
            };

            return (
                <ul className="list-group">
                     {items.map((item) => {
                         const isExpanded = expandedItems[item.key]
                         return (
                            <li key={item.key} className={`list-group-item ${getIndentClass(level)}`}
                                  onDragOver={(e) => handleDragOver(e)}
                                    onDrop={(e) => handleDrop(e, item.key, item.type)}
                               >
                                <a
                                     href="#"
                                      style={{ display: "flex", alignItems: "center", lineHeight: "1.2", textDecoration: "none", color: "black" }}
                                     onClick={() => toggleItem(item.key)}
                                      draggable
                                        onDragStart={(e) => handleDragStart(e, item.key)}
                                 >
                                     <span style={{ flex: 1, overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap" }}> {item.label}</span>
                                      {item.children && <i className={`fas ${isExpanded ? 'fa-minus-square' : 'fa-plus-square'}`}></i>}
                                 </a>
                                   {item.hoverLabel &&  <div
                                        style={{ width: "50%", border: item.hoverLabel ? "1px solid #ccc" : "none" , padding:"2px", position: 'relative' }}
                                          onClick={() => handleClickInput(item.key, item.hoverLabel || "", item.label || "")}
                                         >
                                               {getDisplayText(item.hoverLabel)}
                                      </div>}
                                    {item.hoverLabel && <input
                                        type="text"
                                        value={item.hoverLabel || ""}
                                            style={{ display: "none" }}
                                               ref={setInputRef(item.key)}
                                        />}
                                   <ul className={`nested-list ${isExpanded ? 'show' : ''}`} >
                                        {item.children && renderNestedList(item.children, level + 1)}
                                    </ul>
                            </li>
                         );
                     })}
                </ul>
              );
         };


         if (!data) return <p>Table will be displayed here after uploading a file.</p>;
        return renderNestedList(data);
};

export default NestedList;
