import React from 'react';

const ConfirmationDialog = ({ isOpen, title, message, confirmText, cancelText, onConfirm, onCancel }) => {
  console.log("ConfirmationDialog rendering with props:", { isOpen, title, message });
  
  if (!isOpen) {
    console.log("Dialog not open, returning null");
    return null;
  }
  
  console.log("Dialog is open, rendering content");
  
  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50 backdrop-filter backdrop-blur-sm">
      <div className="bg-white p-6 rounded-lg shadow-xl max-w-md w-full animate-scale-in">
        <h3 className="text-xl font-bold text-gray-800 mb-2">{title}</h3>
        <p className="text-gray-600 mb-6">{message}</p>
        <div className="flex justify-end gap-3">
          <button 
            onClick={() => {
              console.log("Cancel button clicked");
              if (typeof onCancel === 'function') {
                onCancel();
              }
            }}
            className="px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-md text-gray-800 transition-colors"
          >
            {cancelText || "Cancel"}
          </button>
          <button 
            onClick={() => {
              console.log("Confirm button clicked");
              if (typeof onConfirm === 'function') {
                onConfirm();
              }
            }}
            className="px-4 py-2 bg-gradient-to-r from-rose-600 to-red-500 hover:from-rose-700 hover:to-red-600 text-white rounded-md transition-colors"
          >
            {confirmText || "Confirm"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ConfirmationDialog;
