import React from "react";

const TableVisualization = ({ graphData, onDragEnd }) => {
  const handleDragStart = (event, node) => {
    event.dataTransfer.setData("nodeKey", node.key);
    event.dataTransfer.setData("nodeType", node.category);
  };

  const handleDrop = (event, targetNode) => {
    const sourceKey = event.dataTransfer.getData("nodeKey");
    const sourceType = event.dataTransfer.getData("nodeType");
    const targetKey = targetNode.key;
    const targetType = targetNode.category;

    if (onDragEnd) {
      onDragEnd(sourceKey, targetKey, sourceType, targetType);
    }
  };

  const allowDrop = (event) => {
    event.preventDefault();
  };

  const renderRow = (node, index) => (
    <tr key={index}>
      <td
        draggable={node.category === "object" || node.category === "attribute"}
        onDragStart={(event) => handleDragStart(event, node)}
      >
        {node.category === "cluster" && <b>{node.label}</b>}
        {node.category === "object" && node.label}
        {node.category === "attribute" && node.label}
      </td>
    </tr>
  );

  return (
    <div>
      <h3>Table Visualization</h3>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Cluster</th>
            <th>Object</th>
            <th>Attribute</th>
          </tr>
        </thead>
        <tbody>
          {graphData.nodeDataArray.map((node, index) => {
            if (node.category === "cluster") {
              return (
                <tr
                  key={index}
                  onDrop={(event) => handleDrop(event, node)}
                  onDragOver={allowDrop}
                >
                  <td>{node.label}</td>
                  <td colSpan={2}></td>
                </tr>
              );
            }
            return renderRow(node, index);
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TableVisualization;
