import React, { useState, useEffect, useCallback } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  Position
} from 'reactflow';
import 'reactflow/dist/style.css';

// Cluster Node Component
function ClusterNode({ data }) {
  return (
    <div className="cluster-node" 
      style={{
        padding: '10px',
        borderRadius: '5px',
        background: 'linear-gradient(to bottom, #1E40AF, #3B82F6)',
        color: 'white',
        width: '180px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        border: '2px solid #1E3A8A'
      }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        <div style={{ 
          width: '16px', 
          height: '16px', 
          borderRadius: '50%', 
          backgroundColor: 'white' 
        }}></div>
        <div style={{
          fontWeight: 'bold',
          fontSize: '14px',
          textAlign: 'center'
        }}>{data.label}</div>
      </div>
      {data.hoverLabel && (
        <div style={{
          marginTop: '8px',
          fontSize: '12px',
          backgroundColor: 'rgba(255, 255, 255, 0.2)',
          padding: '4px',
          borderRadius: '3px'
        }}>{data.hoverLabel}</div>
      )}
    </div>
  );
}

// Object Node Component
function ObjectNode({ data }) {
  return (
    <div className="object-node"
      style={{
        padding: '10px',
        borderRadius: '8px',
        background: 'linear-gradient(to bottom, #065F46, #10B981)',
        color: 'white',
        width: '160px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        border: '2px solid #064E3B'
      }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        <div style={{
          width: '0',
          height: '0',
          borderLeft: '7px solid transparent',
          borderRight: '7px solid transparent',
          borderBottom: '14px solid white'
        }}></div>
        <div style={{
          fontWeight: 'bold',
          fontSize: '13px'
        }}>{data.label}</div>
      </div>
      {data.hoverLabel && (
        <div style={{
          marginTop: '8px',
          fontSize: '12px',
          backgroundColor: 'rgba(255, 255, 255, 0.2)',
          padding: '4px',
          borderRadius: '3px'
        }}>{data.hoverLabel}</div>
      )}
    </div>
  );
}

// Harmonised Attribute Node Component
function HarmonisedNode({ data }) {
  return (
    <div className="harmonised-node"
      style={{
        padding: '8px',
        borderRadius: '8px',
        background: 'linear-gradient(to bottom, #581C87, #8B5CF6)',
        color: 'white',
        width: '140px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        border: '2px solid #4C1D95'
      }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
        <div style={{
          width: '12px',
          height: '12px',
          transform: 'rotate(45deg)',
          backgroundColor: 'white'
        }}></div>
        <div style={{
          fontSize: '12px'
        }}>{data.label}</div>
      </div>
      {data.hoverLabel && (
        <div style={{
          marginTop: '6px',
          fontSize: '11px',
          backgroundColor: 'rgba(255, 255, 255, 0.2)',
          padding: '3px',
          borderRadius: '3px'
        }}>{data.hoverLabel}</div>
      )}
    </div>
  );
}

const GraphVisualization = ({ graphData, dimensions, setDimensions }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [rfInstance, setRfInstance] = useState(null);

