          position: { x: xPosition, y: yPosition },
          data: { 
            label: harmonisedName,
            hoverLabel: `Harmonised attribute for: ${attributeGroup.map(a => a.label).join(", ")}`
          },
          sourcePosition: Position.Bottom,
          targetPosition: Position.Top
        });
        
        // Add edge from object to harmonised attribute
        flowEdges.push({
          id: `${object.key}-${harmonisedNodeId}`,
          source: object.key,
          target: harmonisedNodeId,
          animated: false,
          style: { stroke: '#555', strokeWidth: 2 },
          type: 'smoothstep'
        });
      });
    });
    
    return { nodes: flowNodes, edges: flowEdges };
  };

  // Save reference to the React Flow instance
  const onInit = useCallback(instance => {
    setRfInstance(instance);
    
    // After rendering, fit the view to see all nodes
    setTimeout(() => {
      instance.fitView({ padding: 0.2 });
      
      // Update dimensions to match the rendered graph
      if (setDimensions) {
        const viewportWidth = instance.viewport.width || 800;
        const viewportHeight = instance.viewport.height || 600;
        
        setDimensions({
          width: viewportWidth,
          height: viewportHeight
        });
      }
    }, 100);
  }, [setDimensions]);

  return (
    <div style={{ width: '100%', height: dimensions ? dimensions.height : '600px' }}>
      {graphData ? (
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onInit={onInit}
          nodeTypes={nodeTypes}
          fitView
          attributionPosition="bottom-left"
        >
          <Controls />
          <MiniMap
            nodeStrokeWidth={3}
            zoomable
            pannable
          />
          <Background variant="dots" gap={12} size={1} />
        </ReactFlow>
      ) : (
        <div style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
          color: "#64748b",
          fontSize: "16px",
          fontStyle: "italic",
          border: "1px solid #e2e8f0",
          borderRadius: "8px",
          background: "#f8fafc"
        }}>
          Graph will be displayed here after uploading a file.
        </div>
      )}
    </div>
  );
};

export default GraphVisualization;
