import React from 'react';
import TableRow from '../../TableRow';
import { handleClickInput, setInputRef } from '../AttributeManagement';
import { 
    processDragDrop,
    handleDragStart,
    handleDragEnd,
    handleDragOver,
    handleDragLeave
} from '../DragDropUtils';

/**
 * Component for rendering the table body with rows
 */
const TableBody = ({ 
    paginatedRows,
    rowSpans,
    clusterCounts,
    objectCounts,
    hoveredRow,
    hoveredCell,
    setHoveredRow,
    setHoveredCell,
    graphData,
    setGraphData,
    fetchGraphData,
    openPopup,
    handleLabelChange,
    inputRefs,
    showHarmonisedColumn
}) => {
    // Function to format display text with ellipsis
    const getDisplayText = (text) => {
        if (!text) return "";
        const lines = text.split('\n');
        const firstTwoLines = lines.slice(0, 2).join('\n');
        
        return (
            <div style={{ position: 'relative', display: 'inline-block', width: "100%" }}>
                <div 
                    style={{ 
                        overflow: 'hidden', 
                        whiteSpace: 'pre-line', 
                        maxWidth: "calc(100% - 20px)", 
                        marginRight: "2px",
                        textOverflow: "ellipsis",
                        display: "-webkit-box",
                        WebkitLineClamp: 2,
                        WebkitBoxOrient: "vertical"
                    }}
                    dangerouslySetInnerHTML={{ __html: firstTwoLines.substring(0, 100) }}
                />
                {text && text.length > 100 && (
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        width="16" 
                        height="16" 
                        viewBox="0 0 24 24" 
                        fill="none" 
                        stroke="currentColor" 
                        strokeWidth="2" 
                        strokeLinecap="round" 
                        strokeLinejoin="round"
                        style={{ 
                            position: 'absolute', 
                            bottom: '2px', 
                            right: '2px', 
                            cursor: "pointer",
                            color: "#64748b"
                        }}
                    >
                        <circle cx="11" cy="11" r="8" />
                        <line x1="21" y1="21" x2="16.65" y2="16.65" />
                        <line x1="11" y1="8" x2="11" y2="14" />
                        <line x1="8" y1="11" x2="14" y2="11" />
                    </svg>
                )}
            </div>
        );
    };

    return (
        <tbody>
            {paginatedRows.map((row, index) => (
                <TableRow 
                    key={row.attr.key}
                    row={row}
                    index={index}
                    rowSpans={rowSpans}
                    clusterCounts={clusterCounts}
                    objectCounts={objectCounts}
                    hoveredRow={hoveredRow}
                    hoveredCell={hoveredCell}
                    setHoveredRow={setHoveredRow}
                    setHoveredCell={setHoveredCell}
                    handleDragStart={(event, key, category) => 
                        handleDragStart(event, key, category, setDraggedItem)
                    }
                    handleDragEnd={(event) => 
                        handleDragEnd(event, setDraggedItem)
                    }
                    handleDragOver={handleDragOver}
                    handleDragLeave={handleDragLeave}
                    handleDrop={(event, targetKey, targetCategory) => 
                        processDragDrop(
                            event, 
                            targetKey, 
                            targetCategory, 
                            graphData, 
                            setGraphData, 
                            fetchGraphData
                        )
                    }
                    openPopup={openPopup}
                    handleClickInput={(key, currentLabel, currentName) => 
                        handleClickInput(
                            inputRefs, 
                            openPopup, 
                            key, 
                            currentLabel, 
                            currentName
                        )
                    }
                    handleLabelChange={handleLabelChange}
                    setInputRef={setInputRef(inputRefs)}
                    getDisplayText={getDisplayText}
                    showHarmonisedColumn={showHarmonisedColumn}
                />
            ))}
        </tbody>
    );
};

export default TableBody;
