import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import * as go from "gojs";

const App = () => {
  const [file, setFile] = useState(null);
  const [graphData, setGraphData] = useState(null);
  const [popup, setPopup] = useState({ isOpen: false, key: null, value: "" });
  const diagramRef = useRef();

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!file) {
      alert("Please select a file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(response.data.message);
      fetchGraphData();
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Error uploading file. Please check the console for more details.");
    }
  };

  const fetchGraphData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/graph-data/");
      setGraphData(response.data);
    } catch (error) {
      console.error("Error fetching graph data:", error);
      alert("Error fetching graph data. Please check the console for more details.");
    }
  };

  const handleDragStart = (event, key, type) => {
    event.dataTransfer.setData("nodeKey", key);
    event.dataTransfer.setData("nodeType", type);
  };

  const handleDrop = (event, targetKey, targetType) => {
    const nodeKey = event.dataTransfer.getData("nodeKey");
    const nodeType = event.dataTransfer.getData("nodeType");

    if (
      (nodeType === "attribute" && targetType === "object") ||
      (nodeType === "object" && targetType === "cluster")
    ) {
      setGraphData((prev) => {
        const updatedLinks = prev.linkDataArray.filter((link) => link.to !== nodeKey);
        updatedLinks.push({ from: targetKey, to: nodeKey });

        return { ...prev, linkDataArray: updatedLinks };
      });
    }
  };

  const renderTable = () => {
    if (!graphData) return <p>Table will be displayed here after uploading a file.</p>;

    const clusters = graphData.nodeDataArray.filter((node) => node.category === "system");
    const objects = graphData.nodeDataArray.filter((node) => node.category === "object");
    const attributes = graphData.nodeDataArray.filter((node) => node.category === "attribute");

    return (
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Cluster</th>
            <th>Object</th>
            <th>Attribute</th>
          </tr>
        </thead>
        <tbody>
          {attributes.map((attr) => {
            const parentObject = objects.find((obj) =>
              graphData.linkDataArray.some((link) => link.from === obj.key && link.to === attr.key)
            );
            const parentCluster = parentObject
              ? clusters.find((cluster) =>
                  graphData.linkDataArray.some((link) => link.from === cluster.key && link.to === parentObject.key)
                )
              : null;

            return (
              <tr key={attr.key}>
                <td
                  onDragOver={(e) => e.preventDefault()}
                  onDrop={(e) => handleDrop(e, parentCluster?.key, "cluster")}
                  style={{ border: "1px solid black", padding: "5px" }}
                >
                  {parentCluster?.label || "Unlinked"}
                </td>
                <td
                  draggable
                  onDragStart={(e) => handleDragStart(e, parentObject?.key, "object")}
                  onDragOver={(e) => e.preventDefault()}
                  onDrop={(e) => handleDrop(e, parentObject?.key, "object")}
                  style={{ border: "1px solid black", padding: "5px" }}
                >
                  {parentObject?.label || "Unlinked"}
                </td>
                <td
                  draggable
                  onDragStart={(e) => handleDragStart(e, attr.key, "attribute")}
                  style={{ border: "1px solid black", padding: "5px" }}
                >
                  {attr.label}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  };

  useEffect(() => {
    if (graphData) {
      const $ = go.GraphObject.make;

      if (diagramRef.current) {
        const existingDiagram = go.Diagram.fromDiv(diagramRef.current);
        if (existingDiagram) existingDiagram.div = null;
      }

      const myDiagram = $(go.Diagram, diagramRef.current, {
        "undoManager.isEnabled": true,
        layout: $(go.TreeLayout, { angle: 0, layerSpacing: 35 }),
      });

      myDiagram.nodeTemplate = $(
        go.Node,
        "Auto",
        $(go.Shape, "RoundedRectangle", { fill: "lightblue" }, new go.Binding("fill", "color")),
        $(go.TextBlock, { margin: 5 }, new go.Binding("text", "label"))
      );

      myDiagram.model = new go.GraphLinksModel(graphData.nodeDataArray, graphData.linkDataArray);
    }
  }, [graphData]);

  return (
    <div style={{ display: "flex", width: "100%", height: "100%" }}>
      <div style={{ width: "50%", overflowY: "auto", padding: "10px" }}>
        <h2>Node Table</h2>
        {renderTable()}
      </div>
      <div style={{ width: "50%", padding: "10px" }}>
        <h2>Graph Visualization</h2>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleFileUpload}>Upload File</button>
        <div ref={diagramRef} style={{ width: "100%", height: "600px", border: "1px solid black" }} />
      </div>
    </div>
  );
};

export default App;
