import React, { useRef, useEffect, useCallback } from "react";

const Popup = ({ isOpen, nodeName, label, onChangeName, onChangeLabel, onSave, onClose }) => {
  const popupRef = useRef(null);
  const nameInputRef = useRef(null);
  const labelInputRef = useRef(null);


    const handleClickOutside = useCallback((event) => {
        if (popupRef.current && !popupRef.current.contains(event.target)) {
          event.preventDefault();
          event.stopPropagation();
           return;
         }

    }, [popupRef]);


    const handleClickInside = useCallback((event) => {
         if (popupRef.current && event.target !== nameInputRef.current && event.target !== labelInputRef.current) {
             event.preventDefault();
           event.stopPropagation();
             return;
        }
        }, [popupRef, nameInputRef, labelInputRef]);

      useEffect(() => {
         if (isOpen) {
            document.addEventListener('mousedown', handleClickOutside, true);
             }
         return () => {
            document.removeEventListener('mousedown', handleClickOutside, true);
          };
      }, [isOpen, handleClickOutside]);


  if (!isOpen) return null;


  return (
      <div
          style={{
              position: "fixed",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              background: "white",
              border: "1px solid black",
              padding: "20px",
              zIndex: 1000,
          }}
          ref={popupRef}
            onMouseDown={handleClickInside}
      >
            <h3>Edit Node</h3>
            <label htmlFor="nodeName">Node Name:</label>
            <input
                type="text"
                id="nodeName"
                ref={nameInputRef}
                value={nodeName}
                onChange={onChangeName}
                style={{ width: "100%", marginBottom: "10px" }}
              />
            <label htmlFor="nodeLabel">Label:</label>
            <textarea
                rows="5"
                cols="40"
                id="nodeLabel"
                ref={labelInputRef}
                value={label}
                onChange={onChangeLabel}
                 style={{ width: "100%", marginBottom: "10px" }}
            />
          <br />
          <button onClick={onSave}>Save</button>
          <button onClick={onClose} style={{ marginLeft: "10px" }}>
              Cancel
          </button>
      </div>
  );
};

export default Popup;
