import React, { useState, useRef, useEffect } from "react";
import { buttonStyle } from "../utils/styles";

/**
 * FileUpload component handles file selection, validation, and upload preparation
 * This is a comprehensive version with additional features like:
 * - File type validation
 * - File size checking
 * - Drag and drop support
 * - Preview of selected file
 * - Error handling
 */
const FileUpload = ({ isLoading, handleFileChange, handleFileUpload }) => {
  // State for managing component
  const [selectedFile, setSelectedFile] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [fileError, setFileError] = useState(null);
  const [fileValid, setFileValid] = useState(false);
  const fileInputRef = useRef(null);

  // Accepted file types
  const acceptedFileTypes = [
    "text/csv",
    "application/vnd.ms-excel",
    "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
  ];
  
  // Max file size (10MB)
  const maxFileSize = 10 * 1024 * 1024;

  // Reset error state when isLoading changes
  useEffect(() => {
    if (isLoading) {
      setFileError(null);
    }
  }, [isLoading]);

  // Validate the selected file
  const validateFile = (file) => {
    // Reset previous errors
    setFileError(null);
    setFileValid(false);

    // Validate file existence
    if (!file) {
      setFileError("No file selected");
      return false;
    }

    // Validate file type
    const fileType = file.type;
    if (!acceptedFileTypes.includes(fileType) && 
        !file.name.endsWith('.csv') && 
        !file.name.endsWith('.xlsx') && 
        !file.name.endsWith('.xls')) {
      setFileError("Invalid file type. Please select a CSV or Excel file.");
      return false;
    }

    // Validate file size
    if (file.size > maxFileSize) {
      setFileError(`File is too large. Maximum size is ${maxFileSize / (1024 * 1024)}MB.`);
      return false;
    }

    // File is valid
    setFileValid(true);
    return true;
  };

  // Handle file input change
  const handleFileInputChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    
    if (validateFile(file)) {
      // Call the parent component's handler with the event
      handleFileChange(e);
    }
  };

  // Handle drag events
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  // Handle drop event
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      setSelectedFile(file);
      
      if (validateFile(file)) {
        // Create a synthetic event to pass to the parent handler
        const syntheticEvent = {
          target: {
            files: [file]
          }
        };
        handleFileChange(syntheticEvent);
      }
    }
  };

  // Trigger file selection
  const triggerFileSelect = () => {
    fileInputRef.current.click();
  };

  // Perform file upload
  const onUpload = () => {
    if (!selectedFile) {
      setFileError("Please select a file first");
      return;
    }
    
    if (!fileValid) {
      validateFile(selectedFile);
      return;
    }
    
    // Call the parent component's upload handler
    handleFileUpload();
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "12px" }}>
      {/* File drop area */}
      <div 
        style={{
          border: `2px dashed ${dragActive ? "#0f766e" : "#cbd5e1"}`,
          borderRadius: "8px",
          padding: "16px",
          textAlign: "center",
          backgroundColor: dragActive ? "rgba(15, 118, 110, 0.05)" : "transparent",
          transition: "all 0.2s ease",
          cursor: "pointer",
          marginBottom: "8px",
          position: "relative"
        }}
        onClick={triggerFileSelect}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
      >
        {/* Hidden file input */}
        <input
          type="file"
          ref={fileInputRef}
          style={{ display: "none" }}
          onChange={handleFileInputChange}
          accept=".csv,.xlsx,.xls"
        />

        {/* Upload icon */}
        <div style={{ marginBottom: "8px" }}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="32"
            height="32"
            viewBox="0 0 24 24"
            fill="none"
            stroke={dragActive ? "#0f766e" : "#64748b"}
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
            style={{ margin: "0 auto" }}
          >
            <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
            <polyline points="17 8 12 3 7 8"></polyline>
            <line x1="12" y1="3" x2="12" y2="15"></line>
          </svg>
        </div>

        {/* Instruction text */}
        <div style={{ 
          fontSize: "14px", 
          color: dragActive ? "#0f766e" : "#64748b",
          fontWeight: dragActive ? "500" : "normal"
        }}>
          Drag and drop a CSV or Excel file here, or click to browse
        </div>

        {/* Selected file info */}
        {selectedFile && (
          <div style={{ 
            marginTop: "8px", 
            fontSize: "13px", 
            color: "#334155",
            fontWeight: "500"
          }}>
            {selectedFile.name} ({(selectedFile.size / 1024).toFixed(2)} KB)
          </div>
        )}
      </div>

      {/* Error message */}
      {fileError && (
        <div style={{ 
          color: "#ef4444", 
          fontSize: "13px", 
          marginBottom: "8px"
        }}>
          {fileError}
        </div>
      )}

      {/* Action buttons */}
      <div style={{ display: "flex", alignItems: "center", gap: "8px" }}>
        <button
          onClick={triggerFileSelect}
          disabled={isLoading}
          style={{
            ...buttonStyle(isLoading),
            backgroundColor: "#64748b",
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="16"
            height="16"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
            <polyline points="17 8 12 3 7 8"></polyline>
            <line x1="12" y1="3" x2="12" y2="15"></line>
          </svg>
          Select File
        </button>

        <button
          onClick={onUpload}
          disabled={isLoading || !selectedFile || !fileValid}
          style={{
            ...buttonStyle(isLoading || !selectedFile || !fileValid),
            backgroundColor: "#0f766e",
            opacity: (!selectedFile || !fileValid) && !isLoading ? 0.5 : 1
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="16"
            height="16"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
            <polyline points="17 8 12 3 7 8"></polyline>
            <line x1="12" y1="3" x2="12" y2="15"></line>
          </svg>
          Upload
        </button>
      </div>
    </div>
  );
};

export default FileUpload;
