import React from 'react';
import { getUniqueCategories } from '../FilterSortUtils';

/**
 * Component for rendering search and filter controls
 */
const TableFilters = ({ 
    searchTerm, 
    setSearchTerm, 
    filterCategory, 
    setFilterCategory, 
    sortedRows,
    showHarmonisedColumn,
    toggleHarmonisedColumn
}) => {
    return (
        <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            marginBottom: '16px',
            gap: '16px'
        }}>
            {/* Search Input */}
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                flex: 1 
            }}>
                <input 
                    type="text"
                    placeholder="Search nodes..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    style={{
                        width: '100%',
                        padding: '8px 12px',
                        border: '1px solid #cbd5e1',
                        borderRadius: '4px',
                        fontSize: '0.875rem'
                    }}
                />
            </div>

            {/* Category Filter */}
            <select 
                value={filterCategory}
                onChange={(e) => setFilterCategory(e.target.value)}
                style={{
                    padding: '8px 12px',
                    border: '1px solid #cbd5e1',
                    borderRadius: '4px',
                    fontSize: '0.875rem'
                }}
            >
                <option value="all">All Categories</option>
                <option value="unlinked">Unlinked</option>
                {getUniqueCategories(sortedRows).clusters.map(cluster => (
                    <option key={`cluster-${cluster}`} value={cluster}>
                        Cluster: {cluster}
                    </option>
                ))}
                {getUniqueCategories(sortedRows).objects.map(object => (
                    <option key={`object-${object}`} value={object}>
                        Object: {object}
                    </option>
                ))}
            </select>
            
            {/* Harmonise Attributes Button */}
            <button 
                onClick={toggleHarmonisedColumn}
                style={{
                    padding: '8px 12px',
                    backgroundColor: showHarmonisedColumn ? '#1e40af' : '#3b82f6',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    fontSize: '0.875rem',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '6px'
                }}
            >
                <svg 
                    xmlns="http://www.w3.org/2000/svg" 
                    width="16" 
                    height="16" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor" 
                    strokeWidth="2" 
                    strokeLinecap="round" 
                    strokeLinejoin="round"
                >
                    <line x1="8" y1="6" x2="21" y2="6"></line>
                    <line x1="8" y1="12" x2="21" y2="12"></line>
                    <line x1="8" y1="18" x2="21" y2="18"></line>
                    <line x1="3" y1="6" x2="3.01" y2="6"></line>
                    <line x1="3" y1="12" x2="3.01" y2="12"></line>
                    <line x1="3" y1="18" x2="3.01" y2="18"></line>
                </svg>
                Harmonise Attributes
            </button>
        </div>
    );
};

export default TableFilters;
