import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import * as go from "gojs"; // Import GoJS for diagram visualization

const App = () => {
  const [file, setFile] = useState(null);
  const [graphData, setGraphData] = useState(null);
  const [dimensions, setDimensions] = useState({ width: "100%", height: "600px" });
  const diagramRef = useRef();

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!file) {
      alert("Please select a file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(response.data.message);
      fetchGraphData();
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Error uploading file. Please check the console for more details.");
    }
  };

  const fetchGraphData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/graph-data/");
      setGraphData(response.data);
    } catch (error) {
      console.error("Error fetching graph data:", error);
      alert("Error fetching graph data. Please check the console for more details.");
    }
  };

  useEffect(() => {
    if (graphData) {
      const $ = go.GraphObject.make;

      // Dispose of the old diagram if it exists
      if (diagramRef.current) {
        const existingDiagram = go.Diagram.fromDiv(diagramRef.current);
        if (existingDiagram) {
          existingDiagram.div = null; // Clears the previous diagram instance
        }
      }

      const myDiagram = $(go.Diagram, diagramRef.current, {
        "undoManager.isEnabled": true,
        layout: $(go.TreeLayout, { angle: 0, layerSpacing: 35 }),
      });

      myDiagram.nodeTemplate = $(
        go.Node,
        "Auto",
        $(go.Shape, "RoundedRectangle", { fill: "lightblue" }, new go.Binding("fill", "color")),
        $(
          go.Panel,
          "Vertical",
          { margin: 5 },
          $(
            go.TextBlock,
            {
              margin: 2,
              editable: true,
              font: "bold 12pt sans-serif",
            },
            new go.Binding("text", "label").makeTwoWay()
          ),
          $(go.Panel, "Vertical", { name: "DETAILS", visible: true },
            $(
              go.TextBlock,
              {
                margin: 2,
                font: "italic 10pt sans-serif",
                stroke: "gray",
                wrap: go.TextBlock.WrapFit,
                maxSize: new go.Size(300, Infinity),
                textAlign: "center",
              },
              new go.Binding("text", "hoverLabel")
            )
          ),
          $(go.Panel, "Horizontal",
            $(
              "Button",
              { margin: 2, click: (e, obj) => obj.part.findObject("DETAILS").visible ^= true },
              $(go.TextBlock, "Toggle Labels")
            )
          )
        )
      );

      myDiagram.model = new go.GraphLinksModel(graphData.nodeDataArray, graphData.linkDataArray);

      myDiagram.addDiagramListener("InitialLayoutCompleted", () => {
        const bounds = myDiagram.documentBounds;
        setDimensions({
          width: `${Math.ceil(bounds.width)}px`,
          height: `${Math.ceil(bounds.height)}px`,
        });
      });
    }
  }, [graphData]);

  const handleGoButtonClick = () => {
    if (!graphData) {
      alert("No graph data available.");
      return;
    }

    const updatedNodeDataArray = graphData.nodeDataArray.map((node) => {
      if (node.category === "attribute") {
        const randomValue = Math.floor(Math.random() * 99) + 1; // Random number between 1 and 99
        return randomValue <= 50 ? { ...node, color: "red" } : node;
      }
      return node;
    });

    setGraphData({
      ...graphData,
      nodeDataArray: updatedNodeDataArray,
    });
  };

  return (
    <div style={{ display: "flex", width: "100%", height: "100%" }}>
      {/* Table Visualization */}
      <div style={{ width: "50%", borderRight: "1px solid black", padding: "10px", overflowY: "auto" }}>
        <h2>Node Table</h2>
        {graphData ? (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th style={{ border: "1px solid black", padding: "5px" }}>System</th>
                <th style={{ border: "1px solid black", padding: "5px" }}>Object</th>
                <th style={{ border: "1px solid black", padding: "5px" }}>Attribute</th>
              </tr>
            </thead>
            <tbody>
              {graphData.nodeDataArray.map((node) =>
                node.category === "attribute" ? (
                  <tr key={node.key}>
                    <td style={{ border: "1px solid black", padding: "5px" }}>{node.system_name}</td>
                    <td style={{ border: "1px solid black", padding: "5px" }}>{node.object_name}</td>
                    <td style={{ border: "1px solid black", padding: "5px" }}>{node.label}</td>
                  </tr>
                ) : null
              )}
            </tbody>
          </table>
        ) : (
          <p>Upload a file to see the table.</p>
        )}
      </div>

      {/* Graph Visualization */}
      <div style={{ width: "50%", padding: "10px", overflow: "auto" }}>
        <h2>Graph Visualization</h2>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleFileUpload}>Upload File</button>
        <button onClick={handleGoButtonClick}>GO</button>
        <div
          ref={diagramRef}
          style={{
            width: dimensions.width,
            height: dimensions.height,
            border: "1px solid black",
            overflow: "auto",
          }}
        >
          {!graphData && <p>Graph will be displayed here after uploading a file.</p>}
        </div>
      </div>
    </div>
  );
};

export default App;
