import React, { useState, useEffect, useRef } from "react";
import TableGraphVisualization from "./TableGraphVisualization";

const DiagramPopup = ({ 
    graphData, 
    dimensions, 
    setDimensions, 
    isDiagramMinimized, 
    toggleMinimizeDiagram, 
    toggleDiagram 
}) => {
    const [isMaximized, setIsMaximized] = useState(false);
    const containerRef = useRef(null);
    
    // Sanitize the graph data for safety
    const safeGraphData = graphData || { nodeDataArray: [], linkDataArray: [] };
    
    const toggleMaximize = () => {
        setIsMaximized(prev => !prev);
    };
    
    // Calculate dimensions based on container size
    useEffect(() => {
        if (containerRef.current && !isDiagramMinimized) {
            const resizeObserver = new ResizeObserver(entries => {
                const { width, height } = entries[0].contentRect;
                if (width > 0 && height > 0) {
                    setDimensions({ width: `${width}px`, height: `${height - 50}px` });
                }
            });
            
            resizeObserver.observe(containerRef.current);
            return () => resizeObserver.disconnect();
        }
    }, [isDiagramMinimized, setDimensions]);
    
    // Compute dimensions based on state, using viewport units instead of percentages
    // for more stable positioning
    const computedWidth = isMaximized ? "100vw" : (isDiagramMinimized ? "300px" : (dimensions?.width || "800px"));
    const computedHeight = isMaximized ? "100vh" : (isDiagramMinimized ? "40px" : (dimensions?.height || "600px"));
    
    // Compute fixed position styles based on maximized state
    const positionStyle = isMaximized 
        ? { top: 0, left: 0, right: 0, bottom: 0 } 
        : { 
            bottom: isDiagramMinimized ? "0" : "20px",
            right: isDiagramMinimized ? "0" : "20px"
          };
    
    return (
        <div 
            ref={containerRef}
            style={{
                position: "fixed",
                width: computedWidth,
                height: computedHeight,
                backgroundColor: "white",
                borderRadius: isMaximized ? "0" : "8px",
                boxShadow: "0 4px 20px rgba(0, 0, 0, 0.15)",
                overflow: "hidden",
                zIndex: 1000,
                transition: "all 0.3s ease",
                display: "flex",
                flexDirection: "column",
                ...positionStyle
            }}
        >
            {/* Popup Header */}
            <div style={{ 
                display: "flex", 
                justifyContent: "space-between", 
                alignItems: "center", 
                padding: "14px 16px",
                borderBottom: "1px solid #e5e7eb",
                backgroundColor: "#f1f5f9",
                position: "sticky",
                top: 0,
                zIndex: 10,
                flexShrink: 0 // Prevent header from shrinking
            }}>
                <div style={{ 
                    display: "flex", 
                    alignItems: "center", 
                    gap: "12px" 
                }}>
                    {/* Diamond Icon */}
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        width="20" 
                        height="20" 
                        viewBox="0 0 24 24" 
                        fill="#ef4444" 
                        stroke="#c2410c" 
                        strokeWidth="1.5" 
                        strokeLinecap="round" 
                        strokeLinejoin="round"
                    >
                        <polygon points="12 2 22 12 12 22 2 12 12 2" />
                    </svg>
                    
                    <h3 style={{ 
                        margin: 0, 
                        color: "#1e293b", 
                        fontSize: "1rem", 
                        fontWeight: "600" 
                    }}>
                        Table Visualization
                    </h3>
                </div>
                
                <div style={{ 
                    display: "flex", 
                    gap: "8px" 
                }}>
                    {/* Minimize/Restore Button */}
                    <button
                        onClick={toggleMinimizeDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            padding: "4px",
                            borderRadius: "4px"
                        }}
                        title={isDiagramMinimized ? "Restore" : "Minimize"}
                    >
                        <svg 
                            xmlns="http://www.w3.org/2000/svg" 
                            width="16" 
                            height="16" 
                            viewBox="0 0 24 24" 
                            fill="none" 
                            stroke="#64748b" 
                            strokeWidth="2" 
                            strokeLinecap="round" 
                            strokeLinejoin="round"
                        >
                            {isDiagramMinimized 
                                ? <polyline points="7 13 12 18 17 13"></polyline> 
                                : <polyline points="7 11 12 6 17 11"></polyline>}
                        </svg>
                    </button>
                    
                    {/* Maximize/Restore Button */}
                    {!isDiagramMinimized && (
                        <button
                            onClick={toggleMaximize}
                            style={{
                                background: "none",
                                border: "none",
                                cursor: "pointer",
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                padding: "4px",
                                borderRadius: "4px"
                            }}
                            title={isMaximized ? "Restore" : "Maximize"}
                        >
                            <svg 
                                xmlns="http://www.w3.org/2000/svg" 
                                width="16" 
                                height="16" 
                                viewBox="0 0 24 24" 
                                fill="none" 
                                stroke="#64748b" 
                                strokeWidth="2" 
                                strokeLinecap="round" 
                                strokeLinejoin="round"
                            >
                                {isMaximized ? (
                                    <>
                                        <rect x="4" y="4" width="16" height="16" rx="2" ry="2"></rect>
                                        <line x1="9" y1="9" x2="15" y2="15"></line>
                                        <line x1="15" y1="9" x2="9" y2="15"></line>
                                    </>
                                ) : (
                                    <>
                                        <polyline points="15 3 21 3 21 9"></polyline>
                                        <polyline points="9 21 3 21 3 15"></polyline>
                                        <line x1="21" y1="3" x2="14" y2="10"></line>
                                        <line x1="3" y1="21" x2="10" y2="14"></line>
                                    </>
                                )}
                            </svg>
                        </button>
                    )}
                    
                    {/* Close Button */}
                    <button
                        onClick={toggleDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            padding: "4px",
                            borderRadius: "4px"
                        }}
                        title="Close"
                    >
                        <svg 
                            xmlns="http://www.w3.org/2000/svg" 
                            width="16" 
                            height="16" 
                            viewBox="0 0 24 24" 
                            fill="none" 
                            stroke="#64748b" 
                            strokeWidth="2" 
                            strokeLinecap="round" 
                            strokeLinejoin="round"
                        >
                            <line x1="18" y1="6" x2="6" y2="18"></line>
                            <line x1="6" y1="6" x2="18" y2="18"></line>
                        </svg>
                    </button>
                </div>
            </div>
            
            {/* Popup Content */}
            {!isDiagramMinimized && (
                <div style={{ 
                    flexGrow: 1,
                    overflow: "hidden",
                    position: "relative",
                    display: "flex",
                    flexDirection: "column"
                }}>
                    <TableGraphVisualization
                        graphData={safeGraphData}
                        dimensions={{
                            width: "100%",
                            height: "100%"
                        }}
                    />
                </div>
            )}
        </div>
    );
};

export default DiagramPopup;
