import React, { useState, useEffect, useRef } from "react";
import GraphVisualization from "./GraphVisualization";

const DiagramPopup = ({ 
    graphData, 
    dimensions, 
    setDimensions, 
    isDiagramMinimized, 
    toggleMinimizeDiagram, 
    toggleDiagram 
}) => {
    const [isMaximized, setIsMaximized] = useState(false);
    const [rfInstance, setRfInstance] = useState(null);
    const containerRef = useRef(null);
    
    // Handle React Flow instance reference
    const onInitReactFlow = (instance) => {
        setRfInstance(instance);
    };
    
    const toggleMaximize = () => {
        setIsMaximized(prev => !prev);
    };
    
    // Adjust flow instance when maximized state changes
    useEffect(() => {
        if (rfInstance && !isDiagramMinimized) {
            // Give the component time to resize before fitting view
            setTimeout(() => {
                rfInstance.fitView({ padding: 0.2 });
            }, 150);
        }
    }, [isMaximized, isDiagramMinimized, rfInstance]);
    
    // Calculate dimensions based on container size
    useEffect(() => {
        if (containerRef.current && !isDiagramMinimized) {
            const resizeObserver = new ResizeObserver(entries => {
                const { width, height } = entries[0].contentRect;
                if (width > 0 && height > 0) {
                    setDimensions({ width: `${width}px`, height: `${height - 50}px` });
                }
            });
            
            resizeObserver.observe(containerRef.current);
            return () => resizeObserver.disconnect();
        }
    }, [isDiagramMinimized, setDimensions]);
    
    return (
        <div 
            ref={containerRef}
            style={{
                position: "fixed",
                bottom: isDiagramMinimized ? "0" : "20px",
                right: isDiagramMinimized ? "0" : "20px",
                width: isMaximized ? "100%" : (isDiagramMinimized ? "300px" : "800px"),
                height: isMaximized ? "100%" : (isDiagramMinimized ? "40px" : "600px"),
                backgroundColor: "white",
                borderRadius: isMaximized ? "0" : "8px",
                boxShadow: "0 4px 20px rgba(0, 0, 0, 0.15)",
                overflow: "hidden",
                zIndex: 1000,
                transition: "all 0.3s ease",
                display: "flex",
                flexDirection: "column",
                left: isMaximized ? "0" : "auto",
                top: isMaximized ? "0" : "auto"
            }}
        >
            {/* Popup Header */}
            <div style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                padding: "12px 16px",
                borderBottom: isDiagramMinimized ? "none" : "1px solid #e2e8f0",
                backgroundColor: "#ef4444",
                color: "white",
                borderTopLeftRadius: isMaximized ? "0" : "8px",
                borderTopRightRadius: isMaximized ? "0" : "8px"
            }}>
                <h3 style={{ margin: 0, fontSize: "1rem" }}>VP Preview</h3>
                <div style={{ display: "flex", gap: "8px" }}>
                    {/* Minimize/Maximize Button */}
                    <button
                        onClick={toggleMinimizeDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            color: "white",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            width: "24px",
                            height: "24px"
                        }}
                        title={isDiagramMinimized ? "Expand" : "Minimize"}
                    >
                        {isDiagramMinimized ? (
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                <polyline points="5 15 12 8 19 15"></polyline>
                            </svg>
                        ) : (
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                <polyline points="5 9 12 16 19 9"></polyline>
                            </svg>
                        )}
                    </button>
                    
                    {/* Maximize/Restore Button */}
                    <button
                        onClick={toggleMaximize}
                        style={{
                            background: "none",
                            border: "none",
                            color: "white",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            width: "24px",
                            height: "24px",
                            opacity: isDiagramMinimized ? 0.5 : 1,
                            pointerEvents: isDiagramMinimized ? "none" : "auto"
                        }}
                        title={isMaximized ? "Restore" : "Maximize"}
                        disabled={isDiagramMinimized}
                    >
                        {isMaximized ? (
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                <path d="M8 3H5a2 2 0 0 0-2 2v3"></path>
                                <path d="M21 8V5a2 2 0 0 0-2-2h-3"></path>
                                <path d="M3 16v3a2 2 0 0 0 2 2h3"></path>
                                <path d="M16 21h3a2 2 0 0 0 2-2v-3"></path>
                            </svg>
                        ) : (
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                                <path d="M15 3h6v6"></path>
                                <path d="M9 21H3v-6"></path>
                                <path d="M21 3l-7 7"></path>
                                <path d="M3 21l7-7"></path>
                            </svg>
                        )}
                    </button>
                    
                    {/* Close Button */}
                    <button
                        onClick={toggleDiagram}
                        style={{
                            background: "none",
                            border: "none",
                            color: "white",
                            cursor: "pointer",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            width: "24px",
                            height: "24px"
                        }}
                        title="Close"
                    >
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                            <line x1="18" y1="6" x2="6" y2="18"></line>
                            <line x1="6" y1="6" x2="18" y2="18"></line>
                        </svg>
                    </button>
                </div>
            </div>

            {/* Popup Content */}
            {!isDiagramMinimized && (
                <div style={{ 
                    flexGrow: 1,
                    padding: "0",
                    overflow: "hidden",
                    height: "calc(100% - 50px)"
                }}>
                    <GraphVisualization
                        graphData={graphData}
                        dimensions={isMaximized ? { width: "100%", height: "calc(100vh - 80px)" } : dimensions}
                        setDimensions={setDimensions}
                        onInit={onInitReactFlow}
                    />
                </div>
            )}
        </div>
    );
};

export default DiagramPopup;
