import React from "react";

const GlobalStyles = () => {
    return (
        <style>
            {`
                @keyframes slideIn {
                    from {
                        transform: translateY(30px);
                        opacity: 0;
                    }
                    to {
                        transform: translateY(0);
                        opacity: 1;
                    }
                }
                
                @keyframes spin {
                    0% {
                        transform: rotate(0deg);
                    }
                    100% {
                        transform: rotate(360deg);
                    }
                }
                
                @media (max-width: 767px) {
                    .app-container {
                        padding-top: 48px;
                    }
                }
            `}
        </style>
    );
};

export default GlobalStyles;
