import React, { useState, useEffect } from "react";
import axios from "axios";

const TableVisualization = ({ graphData, handleLabelChange, openPopup, setGraphData, fetchGraphData }) => {
    const [draggedItem, setDraggedItem] = useState(null);
    const [sortedRows, setSortedRows] = useState([]); // NEW LINE

  
    const handleDragStart = (event, key) => {
      event.dataTransfer.setData("text/plain", key);
      setDraggedItem(key);
    };
  
    const handleDragOver = (event) => {
        event.preventDefault();
    };
  
    const handleDrop = async (event, targetKey, targetCategory) => {
        event.preventDefault();
        const sourceKey = event.dataTransfer.getData("text/plain");

        if (sourceKey && sourceKey !== targetKey) {
            setGraphData((prev) => {
                let updatedNodeDataArray = [...prev.nodeDataArray];
                let updatedLinkDataArray = [...prev.linkDataArray];

                // Find the source node and target node.
                const sourceNode = prev.nodeDataArray.find(node => node.key === sourceKey);
                const targetNode = prev.nodeDataArray.find(node => node.key === targetKey);
                   if(sourceNode && targetNode) {
                        // Remove all existing links where the source node is the to
                     updatedLinkDataArray = updatedLinkDataArray.filter(link => link.to !== sourceKey);

                     if (sourceNode.category === "attribute") {
                        // If target is object, create new link
                        if(targetNode.category === "object"){
                            updatedLinkDataArray.push({from: targetKey, to: sourceKey});
                        }
                        // if target is system, check if object exists then create new links
                        else if(targetNode.category === "system") {
                             const linkedObject = updatedNodeDataArray.find(node => 
                                prev.linkDataArray.some(link => link.from === node.key && link.to === sourceKey)
                            )
                            if(linkedObject){
                                 updatedLinkDataArray = updatedLinkDataArray.filter(link => link.from !== linkedObject.key && link.to !== sourceKey);
                            }

                           const objects = updatedNodeDataArray.filter(node => node.category === "object");
                           const objectLinks = updatedLinkDataArray.filter(link => link.to !== sourceKey && objects.some(obj => obj.key === link.from));
                             if(objectLinks.length > 0){
                                const parentObject = objects.find(obj => objectLinks.some(l => l.from === obj.key));
                                if(parentObject){
                                    updatedLinkDataArray.push({from: targetKey, to: parentObject.key})
                                    updatedLinkDataArray.push({from: parentObject.key, to: sourceKey});
                                }
                           }
                        }
                     }

                    //If source is object
                     else if (sourceNode.category === "object"){
                         if (targetNode.category === "system"){
                                updatedLinkDataArray = updatedLinkDataArray.filter(link => link.to !== sourceKey);
                                updatedLinkDataArray.push({from: targetKey, to: sourceKey});
                         }
                     }
                }


                return {
                  ...prev,
                  nodeDataArray: updatedNodeDataArray,
                  linkDataArray: updatedLinkDataArray,
                };
            });

                const sourceNode = graphData.nodeDataArray.find(node => node.key === sourceKey);
                const targetNode = graphData.nodeDataArray.find(node => node.key === targetKey);
             try {
                    const response = await axios.post("http://localhost:8000/apply-drag-drop/",{
                      source: sourceKey,
                      target: targetKey,
                      sourceType: sourceNode.category,
                      targetType: targetNode.category
                    });
                    if(response.data.message === "Drag-and-drop operation completed successfully"){
                         await fetchGraphData()
                    }
                 
                } catch (error) {
                       if (error.response) {
                    // The request was made and the server responded with a status code
                    // that falls out of the range of 2xx
                    console.error("Error applying drag and drop:", error.response.status, error.response.data);
                    alert(`Error applying drag and drop. Status: ${error.response.status}, Message: ${error.response.data.detail}`);
                  } else if (error.request) {
                    // The request was made but no response was received
                    console.error("Error applying drag and drop:", error.request);
                     alert("Error applying drag and drop. No response from server");
                  } else {
                    // Something happened in setting up the request that triggered an Error
                    console.error("Error applying drag and drop:", error.message);
                    alert(`Error applying drag and drop: ${error.message}`);
                  }
                }
        }
        setDraggedItem(null)
    };
    
    // Sort function
     const sortRows = (rows) => {
        return [...rows].sort((a, b) => {
            const clusterA = a.parentCluster?.label || "Unlinked";
            const clusterB = b.parentCluster?.label || "Unlinked";
            const objectA = a.parentObject?.label || "Unlinked";
            const objectB = b.parentObject?.label || "Unlinked";
            const attributeA = a.attr.label;
            const attributeB = b.attr.label;
          
            if (clusterA < clusterB) return -1;
            if (clusterA > clusterB) return 1;
            if (objectA < objectB) return -1;
            if (objectA > objectB) return 1;
            if (attributeA < attributeB) return -1;
            if (attributeA > attributeB) return 1;
            return 0;
        });
    };

    useEffect(() => {
        if(graphData){
              const clusters = graphData.nodeDataArray.filter((node) => node.category === "system");
              const objects = graphData.nodeDataArray.filter((node) => node.category === "object");
              const attributes = graphData.nodeDataArray.filter((node) => node.category === "attribute");
            
              const rows = attributes.map((attr) => {
                const parentObject = objects.find((obj) =>
                  graphData.linkDataArray.some((link) => link.from === obj.key && link.to === attr.key)
                );

                 const parentCluster = parentObject
                  ? clusters.find((sys) =>
                      graphData.linkDataArray.some((link) => link.from === sys.key && link.to === parentObject.key)
                    )
                 : null;
                
                return {attr, parentObject, parentCluster}
            });
        setSortedRows(sortRows(rows));
    }
  }, [graphData]);

  if (!graphData) return <p>Table will be displayed here after uploading a file.</p>;


    return (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
                <tr>
                    <th style={{ border: "1px solid black", padding: "5px" }}>Cluster</th>
                    <th style={{ border: "1px solid black", padding: "5px" }}>Object</th>
                    <th style={{ border: "1px solid black", padding: "5px" }}>Attribute</th>
                </tr>
            </thead>
            <tbody>
              {sortedRows.map((row) => (
                   <tr key={row.attr.key}>
                <td
                    style={{ border: "1px solid black", padding: "5px" }}
                     onDragOver={(e) => handleDragOver(e)}
                     onDrop={(e) => handleDrop(e, row.parentCluster?.key, "system")}
                >
                    <div
                          draggable
                          onDragStart={(e) => handleDragStart(e, row.parentCluster?.key)}
                    >
                     {row.parentCluster?.label || "Unlinked"}
                    </div>
                    <br />
                    <input
                        type="text"
                        value={row.parentCluster?.hoverLabel || ""}
                        onChange={(e) => handleLabelChange(e, row.parentCluster?.key)}
                    />
                    <button onClick={() => openPopup(row.parentCluster?.key, row.parentCluster?.hoverLabel || "")}>+</button>
                </td>
                <td
                    style={{ border: "1px solid black", padding: "5px" }}
                     onDragOver={(e) => handleDragOver(e)}
                     onDrop={(e) => handleDrop(e, row.parentObject?.key, "object")}
                >
                    <div
                          draggable
                           onDragStart={(e) => handleDragStart(e, row.parentObject?.key)}
                    >
                        {row.parentObject?.label || "Unlinked"}
                    </div>
                    <br />
                    <input
                        type="text"
                        value={row.parentObject?.hoverLabel || ""}
                        onChange={(e) => handleLabelChange(e, row.parentObject?.key)}
                    />
                    <button onClick={() => openPopup(row.parentObject?.key, row.parentObject?.hoverLabel || "")}>+</button>
                </td>
                <td style={{ border: "1px solid black", padding: "5px" }}>
                  <div
                     draggable
                      onDragStart={(e) => handleDragStart(e, row.attr.key)}
                  >
                    {row.attr.label}
                  </div>
                    <br />
                    <input
                        type="text"
                        value={row.attr.hoverLabel || ""}
                        onChange={(e) => handleLabelChange(e, row.attr.key)}
                    />
                    <button onClick={() => openPopup(row.attr.key, row.attr.hoverLabel || "")}>+</button>
                </td>
            </tr>
            ))}
            </tbody>
        </table>
    );
};

export default TableVisualization;
