import React from 'react';

const AIThinkingOverlay = ({ isOpen, message, progress }) => {
  if (!isOpen) return null;
  
  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50 backdrop-filter backdrop-blur-sm">
      <div className="bg-white p-8 rounded-lg shadow-xl max-w-md w-full">
        <div className="flex flex-col items-center">
          {/* AI Thinking Animation */}
          <div className="relative w-24 h-24 mb-6">
            {/* Brain Pulse Animation */}
            <div className="absolute inset-0 rounded-full bg-gradient-to-r from-rose-600 to-red-500 opacity-75 animate-pulse"></div>
            
            {/* Neural Network Animation */}
            <div className="absolute inset-0 flex items-center justify-center">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" className="w-12 h-12 text-white">
                <circle cx="6" cy="6" r="2" className="fill-current animate-pulse" />
                <circle cx="18" cy="6" r="2" className="fill-current animate-pulse" style={{ animationDelay: '0.2s' }} />
                <circle cx="6" cy="18" r="2" className="fill-current animate-pulse" style={{ animationDelay: '0.4s' }} />
                <circle cx="18" cy="18" r="2" className="fill-current animate-pulse" style={{ animationDelay: '0.6s' }} />
                <circle cx="12" cy="12" r="3" className="fill-current animate-pulse" style={{ animationDelay: '0.3s' }} />
                <line x1="6" y1="6" x2="12" y2="12" className="stroke-current stroke-2" />
                <line x1="18" y1="6" x2="12" y2="12" className="stroke-current stroke-2" />
                <line x1="6" y1="18" x2="12" y2="12" className="stroke-current stroke-2" />
                <line x1="18" y1="18" x2="12" y2="12" className="stroke-current stroke-2" />
              </svg>
            </div>
          </div>
          
          <h3 className="font-bold text-lg text-gray-800 mb-2">AI Processing</h3>
          <p className="text-center text-gray-600 mb-4">{message || "Analyzing data..."}</p>
          
          {/* Progress Indicator */}
          {progress !== undefined && (
            <div className="w-full bg-gray-200 rounded-full h-2.5 mb-2">
              <div 
                className="bg-gradient-to-r from-rose-600 to-red-500 h-2.5 rounded-full" 
                style={{ width: `${progress}%` }}
              ></div>
            </div>
          )}
          
          {/* Thinking Dots Animation */}
          <div className="flex space-x-1 mt-2">
            <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce"></div>
            <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
            <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AIThinkingOverlay;
