import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import FileUpload from "./components/FileUpload";
import TableVisualization from "./components/TableVisualization";
import GraphVisualization from "./components/GraphVisualization";
import LabelEditor from "./components/LabelEditor"; // NEW LINE

const App = () => {
  const [file, setFile] = useState(null);
  const [graphData, setGraphData] = useState(null);
  // const [popup, setPopup] = useState({ isOpen: false, key: null, value: "" }); // COMMENTED OUT
  const [dimensions, setDimensions] = useState({ width: "100%", height: "600px" });
  // const diagramRef = useRef(); // COMMENTED OUT

  // Handle file change
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  // Handle file upload
  const handleFileUpload = async () => {
    if (!file) {
      alert("Please select a file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(response.data.message);
      fetchGraphData();
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Error uploading file. Please check the console for more details.");
    }
  };

  const fetchGraphData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/graph-data/");
      setGraphData(response.data);
    } catch (error) {
      console.error("Error fetching graph data:", error);
      alert("Error fetching graph data. Please check the console for more details.");
    }
  };



    const handleLabelChange = (event, key) => {
        const updatedValue = event.target.value;
        setGraphData((prev) => ({
        ...prev,
        nodeDataArray: prev.nodeDataArray.map((node) =>
            node.key === key ? { ...node, hoverLabel: updatedValue } : node
        ),
        }));
    };

  return (
    <div style={{ display: "flex", width: "100%", height: "100%" }}>
      <div style={{ width: "50%", borderRight: "1px solid black", padding: "10px", overflowY: "auto" }}>
        <h2>Node Table</h2>
        <TableVisualization
          graphData={graphData}
          handleLabelChange={handleLabelChange}
          // openPopup={openPopup} // COMMENTED OUT
          setGraphData={setGraphData}
        />
        <button onClick={handleFileUpload} style={{ marginTop: "10px" }}>
          Save Changes
        </button>
      </div>
      <div style={{ width: "50%", padding: "10px", overflow: "auto" }}>
        <h2>Graph Visualization</h2>
         <FileUpload onFileChange={handleFileChange} onFileUpload={handleFileUpload} />
        {}
        <GraphVisualization
          graphData={graphData}
          dimensions={dimensions}
          setDimensions={setDimensions}
        />
      </div>

        <LabelEditor // NEW LINE
            graphData={graphData} // NEW LINE
            setGraphData={setGraphData} // NEW LINE
        /> {/* NEW LINE */}
    </div>
  );
};

export default App;
