import React, { useState, useRef } from "react";

const FileUpload = ({ onFileChange, onFileUpload, isLoading }) => {
  const [fileName, setFileName] = useState("");
  const fileInputRef = useRef(null);

  const handleFileInputChange = (event) => {
    const selectedFile = event.target.files[0];
    console.log("File selected in FileUpload:", selectedFile);
    if (selectedFile) {
      setFileName(selectedFile.name);
    } else {
      setFileName("");
    }
    onFileChange(event);
  };

  const handleButtonClick = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  const handleUploadClick = () => {
    console.log("Upload button clicked in FileUpload component");
    onFileUpload();
  };

  // Common button styles to ensure consistent height
  const buttonStyle = {
    height: "38px", // Set a consistent height
    border: "none",
    borderRadius: "4px",
    padding: "0 12px",
    cursor: "pointer",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "0.875rem",
    transition: "all 0.2s ease"
  };

  return (
    <div style={{ 
      display: "flex", 
      alignItems: "center", 
      gap: "8px" 
    }}>
      <div style={{ 
        position: "relative",
        overflow: "hidden"
      }}>
        <button
          type="button"
          onClick={handleButtonClick}
          style={{
            ...buttonStyle,
            backgroundColor: "#e2e8f0",
            color: "#334155",
          }}
        >
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            width="16" 
            height="16" 
            viewBox="0 0 24 24" 
            fill="none" 
            stroke="currentColor" 
            strokeWidth="2" 
            strokeLinecap="round" 
            strokeLinejoin="round"
            style={{ marginRight: "6px" }}
          >
            <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
            <polyline points="17 8 12 3 7 8"></polyline>
            <line x1="12" y1="3" x2="12" y2="15"></line>
          </svg>
          Choose File
        </button>
        <input
          ref={fileInputRef}
          type="file"
          onChange={handleFileInputChange}
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            opacity: 0,
            width: "100%",
            height: "100%",
            cursor: "pointer"
          }}
        />
      </div>
      
      {fileName && (
        <span style={{ 
          fontSize: "0.875rem", 
          color: "#475569",
          maxWidth: "150px",
          whiteSpace: "nowrap",
          overflow: "hidden",
          textOverflow: "ellipsis"
        }}>
          {fileName}
        </span>
      )}
      
      <button 
        onClick={handleUploadClick}
        disabled={isLoading || !fileName}
        style={{
          ...buttonStyle,
          backgroundColor: "#1e40af",
          color: "white",
          opacity: isLoading || !fileName ? 0.7 : 1,
          cursor: isLoading || !fileName ? "not-allowed" : "pointer",
        }}
      >
        {isLoading ? (
          <>
            <svg 
              xmlns="http://www.w3.org/2000/svg"
              width="16" 
              height="16" 
              viewBox="0 0 24 24"
              style={{ 
                marginRight: "6px",
                animation: "spin 1s linear infinite"
              }}
            >
              <circle 
                cx="12" 
                cy="12" 
                r="10" 
                stroke="white" 
                strokeWidth="2" 
                fill="none" 
                strokeDasharray="40" 
                strokeDashoffset="20"
              />
            </svg>
            Uploading...
          </>
        ) : (
          <>
            <svg 
              xmlns="http://www.w3.org/2000/svg" 
              width="16" 
              height="16" 
              viewBox="0 0 24 24" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2" 
              strokeLinecap="round" 
              strokeLinejoin="round"
              style={{ marginRight: "6px" }}
            >
              <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
              <polyline points="7 10 12 15 17 10"></polyline>
              <line x1="12" y1="15" x2="12" y2="3"></line>
            </svg>
            Upload
          </>
        )}
      </button>
      
      <style>
        {`
          @keyframes spin {
            0% {
              transform: rotate(0deg);
            }
            100% {
              transform: rotate(360deg);
            }
          }
        `}
      </style>
    </div>
  );
};

export default FileUpload;
