import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import * as go from "gojs";

const App = () => {
  const [file, setFile] = useState(null);
  const [graphData, setGraphData] = useState(null);
  const [popup, setPopup] = useState({ isOpen: false, key: null, value: "" });
  const [dimensions, setDimensions] = useState({ width: "100%", height: "600px" });
  const diagramRef = useRef();

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!file) {
      alert("Please select a file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(response.data.message);
      fetchGraphData();
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Error uploading file. Please check the console for more details.");
    }
  };

  const fetchGraphData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/graph-data/");
      setGraphData(response.data);
    } catch (error) {
      console.error("Error fetching graph data:", error);
      alert("Error fetching graph data. Please check the console for more details.");
    }
  };

  useEffect(() => {
    if (graphData) {
      const $ = go.GraphObject.make;

      if (diagramRef.current) {
        const existingDiagram = go.Diagram.fromDiv(diagramRef.current);
        if (existingDiagram) {
          existingDiagram.div = null;
        }
      }

      const myDiagram = $(go.Diagram, diagramRef.current, {
        "undoManager.isEnabled": true,
        layout: $(go.TreeLayout, { angle: 0, layerSpacing: 35 }),
      });

      myDiagram.nodeTemplate = $(
        go.Node,
        "Auto",
        $(go.Shape, "RoundedRectangle", { fill: "lightblue" }, new go.Binding("fill", "color")),
        $(go.TextBlock, { margin: 5 }, new go.Binding("text", "label"))
      );

      myDiagram.model = new go.GraphLinksModel(graphData.nodeDataArray, graphData.linkDataArray);

      myDiagram.addDiagramListener("InitialLayoutCompleted", () => {
        const bounds = myDiagram.documentBounds;
        setDimensions({
          width: `${Math.ceil(bounds.width)}px`,
          height: `${Math.ceil(bounds.height)}px`,
        });
      });
    }
  }, [graphData]);

  const openPopup = (key, currentValue) => {
    setPopup({ isOpen: true, key, value: currentValue });
  };

  const closePopup = () => {
    setPopup({ isOpen: false, key: null, value: "" });
  };

  const savePopupChanges = () => {
    const updatedValue = popup.value;
    setGraphData((prev) => ({
      ...prev,
      nodeDataArray: prev.nodeDataArray.map((node) =>
        node.key === popup.key ? { ...node, hoverLabel: updatedValue } : node
      ),
    }));
    closePopup();
  };

  const renderTable = () => {
    if (!graphData) return <p>Table will be displayed here after uploading a file.</p>;

    const clusters = graphData.nodeDataArray.filter((node) => node.category === "system");
    const objects = graphData.nodeDataArray.filter((node) => node.category === "object");
    const attributes = graphData.nodeDataArray.filter((node) => node.category === "attribute");

    const rows = attributes.map((attr) => {
      const parentObject = objects.find((obj) =>
        graphData.linkDataArray.some((link) => link.from === obj.key && link.to === attr.key)
      );

      const parentCluster = parentObject
        ? clusters.find((sys) =>
            graphData.linkDataArray.some((link) => link.from === sys.key && link.to === parentObject.key)
          )
        : null;

      return (
        <tr key={attr.key}>
          <td style={{ border: "1px solid black", padding: "5px" }}>
            <div>{parentCluster?.label || "Unlinked"}</div>
            <div>{parentCluster?.hoverLabel || ""}</div>
          </td>
          <td style={{ border: "1px solid black", padding: "5px" }}>
            <div>
              <span
                style={{
                  display: "inline-block",
                  width: "20px",
                  height: "20px",
                  background: "lightgray",
                  cursor: "grab",
                  marginRight: "5px",
                }}
              ></span>
              {parentObject?.label || "Unlinked"}
            </div>
            <div>{parentObject?.hoverLabel || ""}</div>
          </td>
          <td style={{ border: "1px solid black", padding: "5px" }}>
            <div>
              <span
                style={{
                  display: "inline-block",
                  width: "20px",
                  height: "20px",
                  background: "lightgray",
                  cursor: "grab",
                  marginRight: "5px",
                }}
              ></span>
              {attr.label}
            </div>
            <div>{attr.hoverLabel || ""}</div>
          </td>
        </tr>
      );
    });

    return (
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid black", padding: "5px" }}>Cluster</th>
            <th style={{ border: "1px solid black", padding: "5px" }}>Object</th>
            <th style={{ border: "1px solid black", padding: "5px" }}>Attribute</th>
          </tr>
        </thead>
        <tbody>{rows}</tbody>
      </table>
    );
  };

  return (
    <div style={{ display: "flex", width: "100%", height: "100%" }}>
      <div style={{ width: "50%", borderRight: "1px solid black", padding: "10px", overflowY: "auto" }}>
        <h2>Node Table</h2>
        {renderTable()}
      </div>
      <div style={{ width: "50%", padding: "10px", overflow: "auto" }}>
        <h2>Graph Visualization</h2>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleFileUpload}>Upload File</button>
        <div
          ref={diagramRef}
          style={{
            width: dimensions.width,
            height: dimensions.height,
            border: "1px solid black",
          }}
        >
          {!graphData && <p>Graph will be displayed here after uploading a file.</p>}
        </div>
      </div>
    </div>
  );
};

export default App;
