import React, { useState } from "react";
import axios from "axios";
import * as go from "gojs"; // Import GoJS for diagram visualization

const App = () => {
  const [file, setFile] = useState(null);
  const [graphData, setGraphData] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!file) {
      alert("Please select a file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(response.data.message);
      fetchGraphData();
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Error uploading file. Please check the console for more details.");
    }
  };

  const fetchGraphData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/graph-data/");
      setGraphData(response.data);
    } catch (error) {
      console.error("Error fetching graph data:", error);
      alert("Error fetching graph data. Please check the console for more details.");
    }
  };

  const diagramRef = React.useRef();

  React.useEffect(() => {
    if (graphData) {
      const $ = go.GraphObject.make;
      const myDiagram = $(go.Diagram, diagramRef.current, {
        "undoManager.isEnabled": true,
        layout: $(go.TreeLayout, { angle: 90, layerSpacing: 35 }),
      });

      // Define the node template
      myDiagram.nodeTemplate = $(
        go.Node,
        "Auto",
        $(go.Shape, "RoundedRectangle", { fill: "lightblue" }),
        $(
          go.TextBlock,
          { margin: 5, editable: true },
          new go.Binding("text", "label").makeTwoWay()
        ),
        $(
          go.TextBlock,
          {
            margin: 5,
            editable: false,
            visible: false, // Initially hidden
            isMultiline: true,
            name: "HoverLabel",
            background: "lightyellow",
            stroke: "black",
          },
          new go.Binding("text", "hoverLabel")
        )
      );

      // Show hover label on mouse hover
      myDiagram.nodeTemplate.mouseEnter = (e, node) => {
        const hoverLabel = node.findObject("HoverLabel");
        if (hoverLabel) hoverLabel.visible = true;
      };

      myDiagram.nodeTemplate.mouseLeave = (e, node) => {
        const hoverLabel = node.findObject("HoverLabel");
        if (hoverLabel) hoverLabel.visible = false;
      };

      // Set the diagram model
      myDiagram.model = new go.GraphLinksModel(
        graphData.nodeDataArray.map(node => ({
          ...node,
          hoverLabel: node.hoverLabel || "", // Include hover label if available
        })),
        graphData.linkDataArray
      );
    }
  }, [graphData]);

  return (
    <div>
      <h1>Graph Visualization</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleFileUpload}>Upload File</button>
      <div
        ref={diagramRef}
        style={{ width: "100%", height: "600px", border: "1px solid black" }}
      >
        {!graphData && <p>Graph will be displayed here after uploading a file.</p>}
      </div>
    </div>
  );
};

export default App;
