import React, { useState, useEffect, useRef } from "react";
import TableGraphVisualization from "./TableGraphVisualization";
import { buttonStyle } from "../utils/styles";

/**
 * DiagramPopup Component
 * Renders a popup window with the diagram visualization
 * 
 * This component includes:
 * - Draggable header for moving the popup
 * - Resizable corners for adjusting dimensions
 * - Minimize/maximize functionality
 * - Responsive design with saved dimensions
 */
const DiagramPopup = ({
    graphData,
    dimensions,
    setDimensions,
    isDiagramMinimized,
    toggleMinimizeDiagram,
    toggleDiagram,
    hideControlButtons = false
}) => {
    // References for controlling behavior
    const dragRef = useRef(null);
    const popupRef = useRef(null);
    const [position, setPosition] = useState({ x: window.innerWidth - 620, y: 100 });
    const [isDragging, setIsDragging] = useState(false);
    const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
    const [isResizing, setIsResizing] = useState(false);
    const [resizeDirection, setResizeDirection] = useState(null);
    const [initialSize, setInitialSize] = useState({ width: 0, height: 0 });
    const [initialPosition, setInitialPosition] = useState({ x: 0, y: 0 });
    const [initialMousePosition, setInitialMousePosition] = useState({ x: 0, y: 0 });

    // Save dimensions to localStorage for persistence
    useEffect(() => {
        const savedDimensions = localStorage.getItem("diagramDimensions");
        const savedPosition = localStorage.getItem("diagramPosition");
        
        if (savedDimensions) {
            setDimensions(JSON.parse(savedDimensions));
        }
        
        if (savedPosition) {
            setPosition(JSON.parse(savedPosition));
        }
        
        // Check if position is valid (within window bounds)
        if (position.x < 0 || position.x > window.innerWidth - 400 || 
            position.y < 0 || position.y > window.innerHeight - 300) {
            setPosition({ x: window.innerWidth - 620, y: 100 });
        }
    }, []);

    // Save dimensions when they change
    useEffect(() => {
        if (dimensions.width && dimensions.height) {
            localStorage.setItem("diagramDimensions", JSON.stringify(dimensions));
        }
    }, [dimensions]);

    // Save position when it changes
    useEffect(() => {
        localStorage.setItem("diagramPosition", JSON.stringify(position));
    }, [position]);

    // Resize handler functions
    const handleResize = (direction, value) => {
        if (direction === "width") {
            setDimensions(prev => ({ ...prev, width: `${value}px` }));
        } else if (direction === "height") {
            setDimensions(prev => ({ ...prev, height: `${value}px` }));
        }
    };

    // Mouse events for dragging the popup
    const handleMouseDown = (e) => {
        if (e.button !== 0) return; // Only handle left clicks
        
        setIsDragging(true);
        setDragOffset({
            x: e.clientX - position.x,
            y: e.clientY - position.y
        });
        
        e.preventDefault();
    };

    const handleMouseMove = (e) => {
        if (!isDragging && !isResizing) return;
        
        if (isDragging) {
            // When dragging the entire popup
            const newX = Math.max(0, Math.min(window.innerWidth - parseFloat(dimensions.width), e.clientX - dragOffset.x));
            const newY = Math.max(0, Math.min(window.innerHeight - 50, e.clientY - dragOffset.y));
            
            setPosition({ x: newX, y: newY });
        }
        
        if (isResizing) {
            // When resizing the popup
            const dx = e.clientX - initialMousePosition.x;
            const dy = e.clientY - initialMousePosition.y;
            
            let newWidth, newHeight, newX, newY;
            
            switch (resizeDirection) {
                case "se":
                    newWidth = Math.max(400, initialSize.width + dx);
                    newHeight = Math.max(300, initialSize.height + dy);
                    handleResize("width", newWidth);
                    handleResize("height", newHeight);
                    break;
                case "sw":
                    newWidth = Math.max(400, initialSize.width - dx);
                    newHeight = Math.max(300, initialSize.height + dy);
                    newX = initialPosition.x + initialSize.width - newWidth;
                    handleResize("width", newWidth);
                    handleResize("height", newHeight);
                    setPosition(prev => ({ ...prev, x: newX }));
                    break;
                case "ne":
                    newWidth = Math.max(400, initialSize.width + dx);
                    newHeight = Math.max(300, initialSize.height - dy);
                    newY = initialPosition.y + initialSize.height - newHeight;
                    handleResize("width", newWidth);
                    handleResize("height", newHeight);
                    setPosition(prev => ({ ...prev, y: newY }));
                    break;
                case "nw":
                    newWidth = Math.max(400, initialSize.width - dx);
                    newHeight = Math.max(300, initialSize.height - dy);
                    newX = initialPosition.x + initialSize.width - newWidth;
                    newY = initialPosition.y + initialSize.height - newHeight;
                    handleResize("width", newWidth);
                    handleResize("height", newHeight);
                    setPosition({ x: newX, y: newY });
                    break;
                default:
                    break;
            }
        }
    };

    const handleMouseUp = () => {
        setIsDragging(false);
        setIsResizing(false);
    };

    // Add/remove event listeners for mouse movement
    useEffect(() => {
        document.addEventListener("mousemove", handleMouseMove);
        document.addEventListener("mouseup", handleMouseUp);
        
        return () => {
            document.removeEventListener("mousemove", handleMouseMove);
            document.removeEventListener("mouseup", handleMouseUp);
        };
    }, [isDragging, isResizing, dragOffset, resizeDirection, initialSize, initialPosition, initialMousePosition]);

    // Start resizing from a corner
    const startResize = (e, direction) => {
        e.stopPropagation();
        setIsResizing(true);
        setResizeDirection(direction);
        
        setInitialSize({
            width: parseFloat(dimensions.width),
            height: parseFloat(dimensions.height)
        });
        
        setInitialPosition({ ...position });
        setInitialMousePosition({ x: e.clientX, y: e.clientY });
    };

    // Calculate width and height as numbers for internal use
    const width = isDiagramMinimized ? 280 : parseFloat(dimensions.width);
    const height = isDiagramMinimized ? 40 : parseFloat(dimensions.height);

    // Get styles based on state
    const getStyles = () => {
        const baseStyles = {
            position: "fixed",
            width: `${width}px`,
            height: `${height}px`,
            backgroundColor: "white",
            borderRadius: "8px",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.15)",
            overflow: "hidden",
            display: "flex",
            flexDirection: "column",
            transition: isDragging || isResizing ? "none" : "height 0.3s ease, width 0.3s ease",
            zIndex: 100,
        };

        // Set position based on state
        if (isDiagramMinimized) {
            return {
                ...baseStyles,
                bottom: "20px",
                right: "20px",
                top: "auto",
                left: "auto"
            };
        } else {
            return {
                ...baseStyles,
                left: `${position.x}px`,
                top: `${position.y}px`,
            };
        }
    };

    return (
        <div
            ref={popupRef}
            style={getStyles()}
        >
            {/* Diagram Header - Draggable */}
            <div
                ref={dragRef}
                style={{
                    padding: "8px 12px",
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    borderBottom: isDiagramMinimized ? "none" : "1px solid #e2e8f0",
                    backgroundColor: "#1e40af",
                    color: "white",
                    cursor: isDiagramMinimized ? "default" : "move"
                }}
                onMouseDown={!isDiagramMinimized ? handleMouseDown : undefined}
            >
                <div
                    style={{
                        display: "flex",
                        alignItems: "center",
                        gap: "8px",
                        fontWeight: "500",
                        fontSize: "14px",
                    }}
                >
                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="16"
                        height="16"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                    >
                        <circle cx="12" cy="12" r="10"></circle>
                        <line x1="2" y1="12" x2="22" y2="12"></line>
                        <path d="M12 2a15.3 15.3 0 0 1 4 10 15.3 15.3 0 0 1-4 10 15.3 15.3 0 0 1-4-10 15.3 15.3 0 0 1 4-10z"></path>
                    </svg>
                    Relationship Diagram
                </div>
                <div style={{ display: "flex", gap: "4px" }}>
                    {/* Minimize/Maximize Button */}
                    <button
                        onClick={toggleMinimizeDiagram}
                        style={{
                            ...buttonStyle(false),
                            padding: "4px",
                            minWidth: "24px",
                            height: "24px",
                            backgroundColor: "transparent",
                            border: "1px solid rgba(255, 255, 255, 0.2)",
                        }}
                    >
                        <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="14"
                            height="14"
                            viewBox="0 0 24 24"
                            fill="none"
                            stroke="currentColor"
                            strokeWidth="2"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                        >
                            {isDiagramMinimized ? (
                                <>
                                    <polyline points="6 15 12 9 18 15"></polyline>
                                </>
                            ) : (
                                <>
                                    <polyline points="6 9 12 15 18 9"></polyline>
                                </>
                            )}
                        </svg>
                    </button>
                    {/* Close Button */}
                    <button
                        onClick={toggleDiagram}
                        style={{
                            ...buttonStyle(false),
                            padding: "4px",
                            minWidth: "24px",
                            height: "24px",
                            backgroundColor: "transparent",
                            border: "1px solid rgba(255, 255, 255, 0.2)",
                        }}
                    >
                        <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="14"
                            height="14"
                            viewBox="0 0 24 24"
                            fill="none"
                            stroke="currentColor"
                            strokeWidth="2"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                        >
                            <line x1="18" y1="6" x2="6" y2="18"></line>
                            <line x1="6" y1="6" x2="18" y2="18"></line>
                        </svg>
                    </button>
                </div>
            </div>

            {/* Diagram Content */}
            {!isDiagramMinimized && (
                <div
                    style={{
                        flex: "1",
                        overflow: "auto",
                        position: "relative",
                    }}
                >
                    <TableGraphVisualization
                        graphData={graphData}
                        dimensions={{
                            width: width,
                            height: height - 50, // Adjust for header
                        }}
                        hideControlButtons={hideControlButtons} // Pass the prop to hide control buttons
                    />
                </div>
            )}

            {/* Resize Handles - only show when not minimized */}
            {!isDiagramMinimized && (
                <>
                    {/* SE corner */}
                    <div
                        style={{
                            position: "absolute",
                            bottom: "0",
                            right: "0",
                            width: "20px",
                            height: "20px",
                            cursor: "nwse-resize",
                            background: "transparent",
                            zIndex: 2,
                        }}
                        onMouseDown={(e) => startResize(e, "se")}
                    />
                    {/* SW corner */}
                    <div
                        style={{
                            position: "absolute",
                            bottom: "0",
                            left: "0",
                            width: "20px",
                            height: "20px",
                            cursor: "nesw-resize",
                            background: "transparent",
                            zIndex: 2,
                        }}
                        onMouseDown={(e) => startResize(e, "sw")}
                    />
                    {/* NE corner */}
                    <div
                        style={{
                            position: "absolute",
                            top: "0",
                            right: "0",
                            width: "20px",
                            height: "20px",
                            cursor: "nesw-resize",
                            background: "transparent",
                            zIndex: 2,
                        }}
                        onMouseDown={(e) => startResize(e, "ne")}
                    />
                    {/* NW corner */}
                    <div
                        style={{
                            position: "absolute",
                            top: "0",
                            left: "0",
                            width: "20px",
                            height: "20px",
                            cursor: "nwse-resize",
                            background: "transparent",
                            zIndex: 2,
                        }}
                        onMouseDown={(e) => startResize(e, "nw")}
                    />
                </>
            )}

            {/* Resize indicator - only show when resizing */}
            {isResizing && (
                <div
                    style={{
                        position: "absolute",
                        bottom: "5px",
                        right: "5px",
                        backgroundColor: "rgba(0, 0, 0, 0.7)",
                        color: "white",
                        padding: "2px 6px",
                        borderRadius: "4px",
                        fontSize: "10px",
                        pointerEvents: "none",
                    }}
                >
                    {Math.round(parseFloat(dimensions.width))} Ã— {Math.round(parseFloat(dimensions.height))}
                </div>
            )}
        </div>
    );
};

export default DiagramPopup;
